import React, { useContext, useEffect, useState } from 'react'
import { Link, useParams }  from "react-router-dom";
import VacationDataTable from '../../components/trip/VacationDataTable';
import axios from '../../components/Axios';
import VacationContext from '../../context/VacationContext';
export default function Vacations() {
  const vacationCtx = useContext(VacationContext);
    let { id } = useParams();
    const initialState = {
        loading: false,
        code:null,
        error: null,
      }
    const [consultant, setConsultant] = useState(initialState);
    useEffect(()=>{
        axios.get(`consultants/${id}`).then((result)=>{
            setConsultant({loading: true, code:result.data.data.code, error: null,});

        }).catch((err)=>{setConsultant({loading: true, code:null, error: "consultants/id дуудхад алдаа гарлаа"+err.message,})})
    },[id]);
  return (
    <div className="content-wrapper">
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-10">
              <h1 className="m-0">{consultant.code} Аяллын менежерийн аяллууд</h1>
            </div>
            <div className="col-sm-2">
              <Link to="../vacationForm"><button type="button" className="btn btn-block btn-success">Аялал нэмэх</button></Link>
            </div>
          </div>
        </div>
      </div> 
      <div className="content">
        <div className="container-fluid">
          <div className="row">
            <VacationDataTable mcode={id}/>
          </div>
        </div>
      </div>
    </div>
  )
}
