import React, { useContext, useEffect, useState } from 'react'
import { Link }  from "react-router-dom";
import UserContext from '../context/UserContext';
import axios from '../components/Axios';
  export default function DataTable(props) { 
    const userCtx = useContext(UserContext);
    const initialState = {
      loading: false,
      data:null,
      pageination:null,
      errorCode:null,
      error: null,
    }
    const [usersState, setUsersState] = useState(initialState);
    useEffect(()=>{
      const script = document.createElement('script');
      script.src = 'js/init_datatable.js';
      script.async = true;
      document.body.appendChild(script);

      if(props.cate === '1'){
        setUsersState({...usersState, loading:true});
        axios.get(`users`,{
          headers:{Authorization:` ${userCtx.userState.token}`}
        }).then((result)=>{
          setUsersState({
            loading: false,
            data:result.data.data,
            pageination:result.data.pageination,
            errorCode:null,
            error: null,
          })
        }).catch((err)=>{
          setUsersState({
            loading: false,
            data:null,
            pageination:null,
            errorCode:err.response.data.error.statusCode,
            error: err.response.data.error.MNMessage,
          })
        })
      }      
    },[]);
    console.log("ddd")
    return (
    <div className="col-lg-12">
      <div className="card">
        <div className="card-body">
          <table id="example1" className="table table-bordered table-striped">
            <thead>
              <tr>
                <th>â„–</th>
                <th>Role</th>
                <th>Email</th>
                <th>Lastname</th>
                <th>Firstname</th>
                <th>Phone</th>
                <th>Edit</th>
              </tr>
            </thead>
            <tbody>

              <tr>
                <td>1</td>
                <td>super admin</td>
                <td>admin_admin</td>
                <td>A.DEMCHIGJAV</td>
                <td>90500001</td>
                <td>tengertravel.it@gmail.com</td>
                <td></td>
              </tr>
              <tr>
                <td>2</td>
                <td>admin</td>
                <td>user_user</td>
                <td>TS.BASMAA</td>
                <td>95953939</td>
                <td>tengertravel39@gmail.com</td>
                
                <td><Link to={props.cate === '1' ? "../adminform/2" : "../userform/2"}><button type="button" className="btn btn-block btn-primary btn-sm">Edit</button></Link></td>
              </tr>
            </tbody>
          </table>
        </div>
            {/* /.card-body */}
      </div>
    </div>
    )
}
