import React, { useContext, useState } from 'react';
//import { useNavigate } from 'react-router-dom';
import UserContext from '../context/UserContext';
import Spinner from '../components/spinner'
export default function Login() {
  
  const userCtx = useContext(UserContext);
  const [emailPassword, setEmailPassword] = useState({
    email:null,
    password:null,
    error:null,
  });
  //const navigate = useNavigate();
  const handleType = (e) =>{
    const {name, value} = e.target;
    setEmailPassword((stateBefore)=>({...stateBefore, [name]:value,error:null}));
    userCtx.loginErrorClear();
  }
  const handleSubmit = () =>{
    userCtx.loginUser(emailPassword.email,emailPassword.password);
  }

    return (
      <div className="row" style={{alignItems:"center",justifyContent:"center",minHeight:"60vh"}}>
        <div style={{width:450}}>
        <Spinner />
          
        </div>
      </div>
    )
  }
