import React, { useContext, useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom';
import axios from '../components/Axios'
import Spinner from '../components/spinner';
import UserContext from '../context/UserContext';
export default function UserPassword() {
    const userCtx = useContext(UserContext);
    let { id } = useParams();
    const [state, setState] = useState({loading:false,email:null,code:null,error:null});
    useEffect(()=>{
        if(id){
            //setState({...state, loading:true});
            axios.get('users/'+id,{
              headers:{Authorization:`Bearer ${userCtx.userState.token}`}
            }).then((result)=>{
              console.log(result);
              setState({...state, loading:false,email:result.data.data.email,code:null,error:null});
            }).catch((err)=>{
              console.log(err);
              setState({...state, loading:false,email:null,code:null,error:err.response.data.error.MNMessage});
            });
        }
    },[])
  return (
    <div className="content-wrapper">
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-5">
              <h1>Нууц үг солих</h1>
            </div>{/* /.col */}
            <div className="col-sm-2">
              <Link to="../users"><button type="button" className="btn btn-block btn-primary btn-sm">Буцах</button></Link>
            </div>
          </div>{/* /.row */}
        </div>{/* /.container-fluid */}
      </div> 
      <div className="content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-lg-7">
              {state.loading ? <Spinner /> :
                <div className="card card-primary">
                  <div className="card-header">
                    <h3 className="card-title"><span style={{color:'red', marginRight:5}}>{state.error}</span> Нууц үг солих</h3>
                  </div>
                  <div className="card-body">
                    <hr/>
                    <div className="row">
                      <div className="col-sm-6">
                        <input type="email" className="form-control" name="email" placeholder="email" defaultValue={state.email}/>
                      </div>
                      <div className="col-sm-6">
                        <button className="btn btn-primary" style={{float:'left'}}>Хүсэлт</button>
                      </div>
                    </div>
                  </div>
                </div>
              }
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
