import React, { useContext, useEffect, useRef, useState } from 'react'
import { Link, useParams } from 'react-router-dom';
import axios from '../../components/Axios';
import UserContext from '../../context/UserContext';
import VacationContext from '../../context/VacationContext';
export default function VacationImage() {
  let { id } = useParams();
  const vacationCtx = useContext(VacationContext);
  const userCtx = useContext(UserContext);
  const [vname, setVname] = useState(null);
  const [vbackgroundImage, setVbackgroundImage] = useState(null);
  const [vimages, setVimages] = useState([]);
  const [verror, setVError] = useState(null);
  useEffect(()=>{
    
    if(id){
      axios.get("vacations/"+id).then((result)=>{
        const resultData = result.data.data;
        setVname(resultData.name);
        if(resultData.backgroundImage){setVbackgroundImage(resultData.backgroundImage);}
        setVname(resultData.name);
        setVimages(resultData.images)
      }).catch((err)=>{
        setVError(err); console.log(err)
      })
    }
  },[]);

  const savehandle =(newProvide)=>{
    if(id){
    //   axios.put("vacations/"+id, {provide:[...newProvide]},{
    //     headers:{Authorization:`Bearer ${userCtx.userState.token}`}
    //   }).then((result)=>{
    //   }).catch((err)=>{
    //     setVprovide({name:null,provide:[],error:err})
    //   })
    }
  }
  const deleteImage =(e)=>{
    if(id){
    console.log("d")
    }
  }

  const provideValue = useRef();
  const coverImageAdd = ()=>{
    if(provideValue.current.value){
        // let newnew =[...vprovide.provide]; newnew.push(provideValue.current.value); provideValue.current.value="";
        // setVprovide({name:vprovide.name,provide:newnew,error:null});
        // savehandle(newnew);
    }
  }
  const imageAdd = ()=>{
    if(provideValue.current.value){
        // let newnew =[...vprovide.provide]; newnew.push(provideValue.current.value); provideValue.current.value="";
        // setVprovide({name:vprovide.name,provide:newnew,error:null});
        // savehandle(newnew);
    }
  }

  return (
    <div className="content-wrapper">
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-10">
              <h1 className="m-0">{vacationCtx.vacationState.mcode} кодтой {vname}</h1>
            </div>{/* /.col */}
            <div className="col-sm-2">
              <Link to={`../vacations/${vacationCtx.vacationState.consultantid}`}>
                <button type="button" className="btn btn-block btn-primary btn-sm">Буцах</button>
              </Link>
            </div>
          </div>{/* /.row */}
        </div>{/* /.container-fluid */}
      </div>
      <div className="content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-lg-12">
              <div className="card card-primary">
                <div className="card-header">
                  <h3 className="card-title">Хөтөлбөрийн үндсэн зураг</h3>
                </div>
                <div className="card-body">
                  <div className="row">
                    <div className="col-sm-5">
                      <div className="input-group input-group-sm"> 
                        <div className="input-group">
                            <div className="custom-file">
                              <input type="file" accept='image/*'/>
                            </div>
                            <div className="input-group-append">
                            <button type="button" className="btn btn-info btn-flat" onClick={coverImageAdd}>Нэмэх</button>
                            </div>
                        </div>
                      </div>
                    </div>
                  </div>
                  <div className="row" style={{marginTop:15}}>
                    <div className="col-sm-12">
                      <img src="https://via.placeholder.com/300/000000?text=2" className="img-fluid mb-12" alt="white sample" />
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-12">
              <div className="card card-primary">
                <div className="card-header"><h4 className="card-title">Аяллын альбом</h4></div>
                <div className="card-body">
                  <div className="row">
                    <div className="col-sm-5">
                      <div className="input-group input-group-sm"> 
                        <div className="input-group">
                            <div className="custom-file">
                              <input type="file" className="custom-file-input" multiple="multiple" id="exampleInputFile" accept='image/*'/>
                              <label className="custom-file-label" htmlFor="exampleInputFile">Choose file</label>
                            </div>
                            <div className="input-group-append">
                            <button type="button" className="btn btn-info btn-flat" onClick={imageAdd}>Нэмэх</button>
                            </div>
                        </div>
                      </div>
                    </div>
                  </div>
                  <div className="row" style={{marginTop:15}}>
                    {vimages &&
                      vimages.map((image,index)=>{
                        return(
                        <div key={index} className="col-sm-2">
                          <img src={image} className="img-fluid mb-2" alt="black sample" />
                          <div className="row">
                            <div className="col-sm-6">{index+1}</div>
                            <div className="col-sm-6">
                              <button 
                                style={{position:'absolute',bottom:0, right:0,cursor:'pointer',border:'none',color:'white',backgroundColor:'lightcoral'}}
                                onClick={deleteImage}
                              >
                                Delete
                              </button>
                            </div>
                          </div>
                        </div>     
                        )
                      })
                    }
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
