import React, { useContext, useEffect, useState } from 'react'
import { Link }  from "react-router-dom";
import Spinner from '../spinner'
import UserContext from '../../context/UserContext';
import axios from '../Axios';
  export default function DataTable(props) { 
    const userCtx = useContext(UserContext);
    const [loading, setLoading] = useState(false);
    const [data, setData] = useState([]);
    const [error, setError] = useState(null);
    const [rules , setRules] = useState(props.roles);

    useEffect(()=>{setRules(props.roles);},[props.roles]);

    useEffect(()=>{
      // const script = document.createElement('script'); script.src = 'js/init_datatable.js';  script.async = true; document.body.appendChild(script);
      let roleSort = "";
      if(rules === 'admin'){roleSort="?role=admin&role=manager&role=consultant"}
      else if(rules === 'user'){roleSort="?role=user&role=blog"}
      else if(rules === 'guide'){roleSort="?role=guide"}
      setLoading(true);
      axios.get(`users${roleSort}`,{ headers:{Authorization:`Bearer ${userCtx.userState.token}`}})
      .then((result)=>{
        setLoading(false); setData(result.data.data);
      }).catch((err)=>{
        let errMessage="Алдаа."; if(err.response.data.error){errMessage = err.response.data.error.message;}
        setError(errMessage);
      })      
    },[]);
    return (
    <div className="col-lg-12">
      <div className="card">
        <div className="card-body">
        {usersState.loading ? <Spinner /> :
          <table id="example1" className="table table-bordered table-striped">
            <thead>
              <tr>
                <th>№</th>
                <th>Role</th>
                <th>Email</th>
                <th>Lastname</th>
                <th>Firstname</th>
                <th>Phone</th>
                <th>Edit</th>
                <th>Password</th>
              </tr>
            </thead>
            <tbody>
            <tr style={{display:"none"}}><td></td><td></td><td></td><td></td><td></td><td></td><td></td><td></td></tr>
           {usersState.data && usersState.data.map((option,index) => (
                  <tr key={index}>
                    <td>{index+1}</td>
                    <td>{option.role}</td>
                    <td>{option.email}</td>
                    <td>{option.lastName}</td>
                    <td>{option.firstName}</td>
                    <td>{option.phone}</td>
                    <td>
                      <Link to={`../${usersState.editUrl}/${option._id}`}>
                        <button type="button" className="btn btn-block btn-primary btn-sm">Edit</button>
                      </Link>
                    </td>
                    <td>
                      <Link to={`../userpassword/${option._id}`}>
                        <button type="button" className="btn btn-block btn-warning btn-sm">Password</button>
                      </Link>
                    </td>
                  </tr>
            ))}
            
            </tbody>
          </table>
        }
        </div>
      </div>
    </div>
    )
}
