import React, { useContext, useState } from 'react'
import axios from '../Axios';
import { Link, useParams }  from "react-router-dom";
import UserContext from '../../context/UserContext';
import SmallSpinner from '../smallSpinner';
export default function CommentForm() {
  let { id } = useParams();
  const userCtx = useContext(UserContext);
  const [loading, setLoading] = useState(null);
  const [cateLoading, setCateLoading] = useState(false);
  const [userId, setUserId] = useState(null);
  const [searchNumber, setSearchNumber] = useState(null);
  const [userName, setUserName] = useState({imageUrl:null, name:null});
  const [cate, setCate] = useState(null);
  const [cateValue, setCateValue] = useState([]);
  const [cateId, setCateId] = useState(null);
  const [comment, setComment] = useState(null);
  const [error, setError] = useState(null);
  const phoneSearch = (e) =>{
    if(e.target.value.length === 8){
      axios.get('users?select=_id lastName firstName profileImage&role=user&phone='+e.target.value,{
            headers:{Authorization:`Bearer ${userCtx.userState.token}`}
      }).then((result)=>{
        const resultData = result.data.data[0];
        if(resultData){
          setUserId(resultData._id); setUserName({imageUrl:resultData.profileImage, name:resultData.firstName+" "+resultData.lastName});
        }else{setUserId(null); setUserName({imageUrl:null, name:null}); setSearchNumber(e.target.value); e.target.value="";}
      }).catch((err)=>{console.log(err)})
    }
  }
  const cateSelect = (e)=>{
    setCate(null); setCateValue([]); setCateId(null);
    if(e.target.value === 1){
        console.log(e.target.value)
      setCateLoading(true);
      axios.get('vacations?select=_id name')
      .then((result)=>{
        setCate(1);
        setCateValue(result.data.data);
        setCateLoading(false);
      }).catch((err)=>{setCateLoading(false); console.log(err)})
    }
  }
  const createComment = ()=>{
    
  }
  const saveComment = ()=>{
    
  }
  const deleteComment = ()=>{

  }

  return (
    <div className="content-wrapper">
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-10">
              <h1 className="m-0">Сэтгэгдэл {id ? "засах":"нэмэх"}</h1>
            </div>{/* /.col */}
            <div className="col-sm-2">
              <Link to={`../comment`}>
                <button type="button" className="btn btn-block btn-primary btn-sm">Буцах</button>
              </Link>
            </div>
          </div>{/* /.row */}
        </div>{/* /.container-fluid */}
      </div> 
      <div className="content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-sm-2">
              <label htmlFor="Selectrole">Хэрэглэгч хайх</label>
              <input type="number" className={`form-control`} name="userId" max={8} placeholder="Утасны дугаар" onChange={phoneSearch}/>
            </div>
            <div className="col-sm-3">
              <label htmlFor="Selectrole">Хэрэглэгч</label>
              {userId ?
                <div style={{display:'flex', flexDirection:'row'}}>
                  <div>zurag{userName.imageUrl}</div>
                  <div>:{userName.name}</div>
                </div>
               :<><div style={{color:'red'}}>{searchNumber}</div><div style={{color:'red'}}>Хэргэлэгч олдсонгүй</div></>
              }
            </div>

            <div className="col-sm-3">
              <label htmlFor="Selectrole">Төрөл</label>
              <select className={`form-control${cateValue[0] ? "" : " is-invalid"}`} name="cate" defaultValue={cate} onChange={cateSelect}>
                <option value="">Сонгох</option><option value="1">Аялал</option><option value="2">Мэдээлэл</option><option value="3">Блог</option>
              </select>
            </div>
            <div className="col-sm-4">
              <label htmlFor="Selectrole">Төрөл</label>
              {cateLoading ? <SmallSpinner />:
                <select className={`form-control ${cateId ? "" : " is-invalid"}`} name="cateId" defaultValue={cateId} 
                  onChange={(e)=>{setCateId(e.target.value)}}
                >
                  <option value="">Сонгох</option>
                  {cateValue.map((option,index)=><option key={index} value={option._id}>{option.name}</option>)}
                </select>
              }
              
              
            </div>
          </div>
          <div className="row">
            <div className="col-sm-12">
              <label htmlFor="Selectrole">Сэтгэгдэл</label>
              <textarea className={`form-control ${comment ? "" : " is-invalid"}`} 
                rows={3} placeholder="Сэтгэгдэл" name="v" defaultValue={comment} onChange={(e)=>{setComment(e.target.value)}}
              />
            </div>
          </div>
          <div className="card-footer">{cateId}
          {id ?
            <>
              <button className={`btn btn-primary ${(userId && cate && cateId && comment) ? "" : " disabled"}`}
                disabled={!(userId && cate && cateId && comment)} style={{float:'right'}} onClick={saveComment}>Хадгалах</button>
              <button className="btn btn-primary btn-danger" onClick={deleteComment}>Устгах</button>
            </>
          :
            <button className={`btn btn-primary ${(userId && cate && cateId && comment) ? "" : " disabled"}`} 
              disabled={!(userId && cate && cateId && comment)} style={{float:'right'}} onClick={createComment}>
            Нэмэх</button>
          }
        </div>
        </div>
      </div>
    </div>
  )
}
