import React, { useState, useRef, useEffect, useContext } from 'react'
import { useParams } from 'react-router-dom';
import UserContext from '../../context/UserContext';
import axios from '../Axios';
export default function NewsCoverImage() {
  const userCtx = useContext(UserContext);
  let { id } = useParams();
  const [hostImageUrl] = useState("http://localhost:8000/photos/");
  const [onImage, setOnImage] = useState(hostImageUrl+"no-image.png");
  const [image, setImage] = useState(null);
  const [error, setError] = useState(null);
  const [imageProgress, setImageProgress] = useState(null);
  useEffect(()=>{
    if(id){
      axios.get('news/'+id).then((result)=>{
        setImage(result.data.data.coverUrl);
      }).catch((err)=>{err.response.data.error ? setError(err.response.data.error.MNMessage) : setError("Алдаа.");})
    }
  },[])

  const onSelectImage = (e) =>{
    setError(null);
    const selectedFiles = e.target.files;
    const selectedFilesArray = Array.from(selectedFiles);
    let imagesArray = "";
    if(selectedFilesArray[0].size < 5000000){imagesArray=URL.createObjectURL(selectedFilesArray[0]);}
    if(imagesArray){setOnImage(imagesArray)}
  }
  const imageRef = useRef()
  const imageAdd = () =>{
    if(imageRef.current && imageRef.current.files[0]){
      const fd = new FormData();
      fd.append('file', imageRef.current.files[0], imageRef.current.files[0].name);
      if(id){
        axios({url:"news/"+id+"/coverphoto", method:'PUT', data: fd, headers:{
          Authorization:`Bearer ${userCtx.userState.token}`}
          ,onUploadProgress: progressEvent =>{
            let percentage = Math.floor((progressEvent.loaded / progressEvent.total) * 100);
            setImageProgress(percentage);
          }
        }).then((result)=>{
          setOnImage(hostImageUrl+"no-image.png"); setImageProgress(null);
        }).catch((err)=>{
          err.response.data.error && setVCoverError(err.response.data.error.MNMessage); 
          setOnImage(hostImageUrl+"no-image.png"); setImageProgress(null);
        })
      }
    }
  }
  return (
    <div className="col-lg-12">
      <div className="card card-primary">
        <div className="card-header"><h3 className="card-title">Мэдээллийн үндсэн зураг</h3></div>
        <div className="card-body">
          <div className="row">
            <div className="col-sm-5">
              <section>
                <label 
                  style={{ 
                    display:'flex',justifyContent:'center',alignItems:'center',
                    border:'1px dotted black',borderRadius:'5px',cursor:'pointer',height:'40px'
                  }}
                >Үндсэн зураг сонгох 5MB дээш зураг сонгогдохгүй!
                  <input type="file" ref={imageRef} name="coverimage" style={{display:'none'}} 
                    onChange={onSelectImage} accept='image/jpg, image/jpeg, image/png'
                  />
                </label>
              </section>
            </div>
            {onImage !== hostImageUrl+"no-image.png" && 
              <div className="col-sm-5">
                <button type="button" className="btn btn-info btn-flat" onClick={imageAdd} style={{height:'40px', borderRadius:'5px'}}>
                  Хадгалах
                </button>
              </div>
            }
            {error && <div style={{fontSize:18, color:'red'}}><strong>Алдаа гарлаа!!! {error}</strong></div>}
          </div>
          <div className="row" style={{marginTop:15}}>
            {imageProgress > 0 ?
              <>
                <div className="col-sm-5">
                  <div className="progress">
                    <div className="progress-bar bg-primary progress-bar-striped" role="progressbar" style={{width: `${imageProgress}%`}} />
                  </div>
                </div>
                <div className="col-sm-1"><div style={{marginTop:-5, textAlign:'left'}}>{imageProgress}%</div></div>
              </>
            : onImage && <div className="col-sm-6"><img src={onImage} className="img-fluid mb-12" alt="white sample" /></div>
            }
            {image &&
              <div className="col-sm-6">
                <img src={`${hostImageUrl}news/cover/${image}`} className="img-fluid mb-12" alt="white sample" />
              </div>
            }
          </div>
        </div>
      </div>
    </div>
  )
}
