import React, { useEffect,useState, useContext } from 'react';
import { useParams }  from "react-router-dom";
//import axios from '../Axios';
import Spinner from '../spinner';
//import UserContext from '../../context/UserContext';
import VacationContext from '../../context/VacationContext';

export default function FormVacation() {
  //const userCtx = useContext(UserContext);
  const vacationCtx = useContext(VacationContext);
  //const navigate = useNavigate();
  let { id } = useParams();
  const vacationInachal = {
    loading:false,
    cCode: null,
    consultant: null,
    name: null,
    itinerary: null,
    content: null,
    day: [],
    price: [],
    program: [],
    abstract: [],
    priceIncluded: [],
    error:null,
  }
  const [state, setState] = useState(vacationInachal);
  useEffect(()=>{
    if(id){
      setState(vacationInachal);
    }
  },[id]);
  return (
    <div className="col-lg-12">
    {state.loading ? <Spinner /> :
    <div className="card card-primary">
      <div className="card-header">
        <h3 className="card-title"><span style={{color:'red', marginRight:5}}></span>Аяллын мэдээллийг үнэн зөв, бүрэн бөглөнө үү.</h3>
      </div>
      <div className="card-body">
        <div className="row">
          <div className="col-sm-6">
          <label htmlFor="Selectrole">Аяллын нэр</label>
          <input type="text" className="form-control" name="name" placeholder="Аяллын нэр"/>
          </div>
          <div className="col-sm-6">
            <label htmlFor="Selectcoin">Чиглэл</label>
            <input type="text" className="form-control" name="itinerary" placeholder="Чиглэл"/>
          </div>
        </div>
        <div className="row">
          <div className="col-sm-12">
          <label htmlFor="Selectrole">Аяллын онцлог</label>
          <textarea className="form-control" rows={3} placeholder="Аяллын онцлог" defaultValue={""} />
          </div>
        </div>
      </div>
    </div>
    }
  </div>
  )
}
