import React, { useContext, useEffect, useRef, useState } from 'react'
import { Link, useParams } from 'react-router-dom';
import axios from '../../components/Axios';
import UserContext from '../../context/UserContext';
import VacationContext from '../../context/VacationContext';
export default function About() {
  let { id } = useParams();
  const vacationCtx = useContext(VacationContext);
  const userCtx = useContext(UserContext);
  const [vname, setVname] = useState(null);
  const [hostImageUrl] = useState("http://localhost:8000/photos/");

  const [vbackgroundImage, setVbackgroundImage] = useState(hostImageUrl+"no-image.png");
  const [vnbackgroundImage, setVnbackgroundImage] = useState(null);
  const [vcoverprogress, setVcoverprogress] = useState(null);
  const [vimages, setVimages] = useState([]);
  const [vnimages, setVnimages] = useState([]);
  const [vimagesprogress, setVimagesprogress] = useState(null);
  const [verror, setVError] = useState(null);
  const [vcovererror, setVCoverError] = useState(null);
  useEffect(()=>{
    if(id){
      axios.get("vacations/"+id).then((result)=>{
        const resultData = result.data.data;
        setVname(resultData.name);
        if(resultData.backgroundImage){setVnbackgroundImage(resultData.backgroundImage);}
        setVnimages(resultData.images)
      }).catch((err)=>{
        err.response.data.error && setVCoverError(err.response.data.error.MNMessage);
      })
    }
  },[vcoverprogress,vimagesprogress]);
  const deleteImage =(image)=>{
    setVnimages(vnimages.filter((e) => e !== image));
    if(id){
      axios({url:"vacations/"+id+"/photo", method:'DELETE', data: {imageURL:image}, headers:{
        Authorization:`Bearer ${userCtx.userState.token}`}}).then((result)=>{

      }).catch((err)=>{ 
        err.response.data.error && setVError(err.response.data.error.MNMessage); 
      })
    }
  }

  const coverImageValue = useRef();
  const coverImageAdd = ()=>{
    if(coverImageValue.current && coverImageValue.current.files[0]){
      const fd = new FormData();
      fd.append('file', coverImageValue.current.files[0], coverImageValue.current.files[0].name);
      if(id){
        axios({url:"vacations/"+id+"/coverphoto", method:'PUT', data: fd, headers:{
          Authorization:`Bearer ${userCtx.userState.token}`}
          ,onUploadProgress: progressEvent =>{
            let percentage = Math.floor((progressEvent.loaded / progressEvent.total) * 100);
            setVcoverprogress(percentage);
          }
        }).then((result)=>{
          setVbackgroundImage(hostImageUrl+"no-image.png"); setVcoverprogress(null);
        }).catch((err)=>{
          err.response.data.error && setVCoverError(err.response.data.error.MNMessage); 
          setVbackgroundImage(hostImageUrl+"no-image.png"); setVcoverprogress(null);
        })
      }
    }
  }
  const refimages = useRef();
  const imageAdd = ()=>{
    if(vimages){
      const fd = new FormData();
      for (let file of vimages) {
        fd.append('file', file, file.name);
      }
      if(id){
        axios({url:"vacations/"+id+"/photo", method:'PUT', data: fd, headers:{
          Authorization:`Bearer ${userCtx.userState.token}`}
          ,onUploadProgress: progressEvent =>{
            let percentage = Math.floor((progressEvent.loaded / progressEvent.total) * 100);
            setVimagesprogress(percentage);
          }
        }).then((result)=>{
          setVimages([]); setVimagesprogress(null)
        }).catch((err)=>{
          err.response.data.error && setVError(err.response.data.error.MNMessage); 
          setVimages([]); setVimagesprogress(null)
        })
      }
    }
  }
  const onSelectimage = (e)=>{
    setVError(null);
    const selectedFiles = e.target.files;
    //console.log(selectedFiles[0]);
    const selectedFilesArray = Array.from(selectedFiles);
    const imagesArray = [];
    selectedFilesArray.map((file) =>{
      if(file.size < 3000000){imagesArray.push(file);}
      return null;
    })
    setVimages(imagesArray)
  }
  const onSelectcoverimage = (e)=>{
    setVCoverError(null);
    const selectedFiles = e.target.files;
    const selectedFilesArray = Array.from(selectedFiles);
    let imagesArray = "";
    if(selectedFilesArray[0].size < 5000000){imagesArray=URL.createObjectURL(selectedFilesArray[0]);}
    if(imagesArray){setVbackgroundImage(imagesArray)}
  }

  return (
    <div className="content-wrapper">
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-10">
              <h1 className="m-0">Бидний тухай</h1>
            </div>
          </div>{/* /.row */}
        </div>{/* /.container-fluid */}
      </div>
      <div className="content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-lg-12">
              <div className="card card-primary">
                <div className="card-header">
                  <h3 className="card-title">Хамт олны зураг</h3>
                </div>
                <div className="card-body">
                  <div className="row">
                    <div className="col-sm-5">
                      <section>
                        <label 
                          style={{ 
                            display:'flex',
                            justifyContent:'center',
                            alignItems:'center',
                            border:'1px dotted black',
                            borderRadius:'5px',
                            cursor:'pointer',
                            height:'40px'
                          }}
                        >Зураг сонгох: 5MB дээш зураг сонгогдохгүй!
                          <input type="file" ref={coverImageValue} name="coverimage" style={{display:'none'}} 
                            onChange={onSelectcoverimage} accept='image/jpg, image/jpeg, image/png'
                          />
                        </label>
                      </section>
                    </div>
                    {vbackgroundImage !== hostImageUrl+"no-image.png" && 
                      <div className="col-sm-5">
                        <button type="button" className="btn btn-info btn-flat" onClick={coverImageAdd} 
                          style={{height:'40px', borderRadius:'5px'}}>Хадгалах
                        </button>
                      </div>
                    }
                    {vcovererror && <div style={{fontSize:18, color:'red'}}><strong>Алдаа гарлаа!!! {vcovererror}</strong></div>}
                  </div>
                  <div className="row" style={{marginTop:15}}>
                    {vcoverprogress > 0 ?
                    <>
                      <div className="col-sm-5">
                        <div className="progress">
                          <div className="progress-bar bg-primary progress-bar-striped" role="progressbar" style={{width: `${vcoverprogress}%`}}>
                          </div>
                        </div>
                      </div>
                      <div className="col-sm-1">
                        <div style={{marginTop:-5, textAlign:'left'}}>{vcoverprogress}%</div>
                      </div>
                    </>
                    : vbackgroundImage  &&
                      <div className="col-sm-6">
                        <img src={vbackgroundImage} className="img-fluid mb-12" alt="white sample" />
                      </div>
                    }
                    {vnbackgroundImage &&
                      <div className="col-sm-6">
                        <img src={`${hostImageUrl}vacationcover/${vnbackgroundImage}`} className="img-fluid mb-12" alt="white sample" />
                      </div>
                    }
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-12">
              <div className="card card-primary">
                <div className="card-header"><h4 className="card-title">Аяллын альбом 30 дээш зураг орох боломжгүй</h4></div>
                <div className="card-body">

                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
