import React, { useEffect, useState } from 'react'
import axios from '../Axios';
import Spinner from '../spinner';
export default function NewsDataTable() {
  const [loading, setloading] = useState(false);
  const [news, setNews] = useState([]);
  useEffect(()=>{
    setloading(true);
    axios.get('news').then((result)=>{
      console.log(result.data.data);
      setNews(result.data.data);
      setloading(false);
    }).catch((err)=>{console.log(err);setloading(false);})
  },[])
  return (
    <div className="col-lg-12">
      <div className="card">
        <div className="card-body">
        {loading ? <Spinner /> :
          <table id="example1" className="table table-bordered table-striped">
            <thead>
              <tr>
                <th>№</th><th>Төлөв</th><th>Гарчиг</th><th>Хугацаа</th><th>Үзэлт</th><th>Сэтгэгдэл</th><th>Засах</th>
              </tr>
            </thead>
            <tbody>
              <tr style={{display:"none"}}><td></td><td></td><td></td><td></td><td></td><td></td><td></td></tr>
{
  news.map((news,index)=>{
<tr>
                  <td>1</td>
                  <td></td>
                  <td></td>
                  <td></td>
                  <td></td>
                  <td></td>
                  <td></td>
                </tr>
  })
}
                
            </tbody>
          </table>
        }
        </div>
      </div>
    </div>
  )
}
