import React, { Suspense, useContext, useEffect, useState } from 'react'
import { BrowserRouter, Route, Routes }  from "react-router-dom";
import Home from './page/Home';
import Login from './page/Login';
import Footer from './components/Footer';
import Header from './components/Header';
import Menu from './components/Menu';
import UserContext from './context/UserContext';

const Admin = React.lazy(()=>{return import('./page/Admin');})
const Users = React.lazy(()=>{return import('./page/Users');})
const Adminform = React.lazy(()=>{return import('./page/Adminform');})
const Userform = React.lazy(()=>{return import('./page/Userform');})

export default function App () {
  const userCtx = useContext(UserContext);
  const [login, SetLogin] = useState(false);
  useEffect(()=>{
    console.log(userCtx.state.userId)
    SetLogin(userCtx.state.userId);
  },[])
  return (
  <BrowserRouter>
    <Suspense fallback={<div>Түр хүлээнэ үү....</div>}>
      {login ? (
        <>
          <Header/><Menu/>
          <Routes>
            <Route exact path="/" element={<Home />} />
            <Route exact path="/login" element={<Home />} />
            <Route exact path="/admin" element={<Admin />} />
            <Route exact path="/adminform" element={<Adminform />} />
            <Route exact path="/adminform/:id" element={<Adminform />} />
            <Route exact path="/users" element={<Users />} />
            <Route exact path="/userform" element={<Userform />} />
            <Route exact path="/userform/:id" element={<Userform />} />
          </Routes>
          <Footer/>
        </>
      ):(
        <Routes>
          <Route exact path="/" element={<Login />} />
          <Route exact path="/login" element={<Login />} />
        </Routes>
      )}
    </Suspense>
  </BrowserRouter>
  )
}
