import React, { useContext, useState } from 'react'
import axios from '../Axios';
import { Link, useParams }  from "react-router-dom";
import UserContext from '../../context/UserContext';
export default function CommentForm() {
  let { id } = useParams();
  const userCtx = useContext(UserContext);
  const [loading, setLoading] = useState(null);
  const [userId, setUserId] = useState(null);
  const [userName, setUserName] = useState({imageUrl:null, name:null});
  const [cate, setCate] = useState(null);
  const [cateId, setCateId] = useState(null);
  const [commnet, setComment] = useState(null);
  const [error, setError] = useState(null);
  const phoneSearch = (e) =>{
    if(e.target.value.length === 8){
      axios.get('users/'+id,{
            headers:{Authorization:`Bearer ${userCtx.userState.token}`}
      }).then((result)=>{console.log(result)}).catch((err)=>{console.log(err)})
    }
  }
  return (
    <div className="content-wrapper">
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-10">
              <h1 className="m-0">Сэтгэгдэл {id ? "засах":"нэмэх"}</h1>
            </div>{/* /.col */}
            <div className="col-sm-2">
              <Link to={`../comment`}>
                <button type="button" className="btn btn-block btn-primary btn-sm">Буцах</button>
              </Link>
            </div>
          </div>{/* /.row */}
        </div>{/* /.container-fluid */}
      </div> 
      <div className="content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-sm-2">
              <label htmlFor="Selectrole">Хэрэглэгч хайх</label>
              <input type="number" className={`form-control ${userId ? "" : " is-invalid"}`} name="userId" max={8} placeholder="Утасны дугаар" defaultValue={userId} onChange={phoneSearch}/>
            </div>
            <div className="col-sm-2">
              <label htmlFor="Selectrole">Хэрэглэгч</label>
              <div style={{display:'flex', flexDirection:'row'}}>
                <div>zurag{userName.imageUrl}</div>
                <div>:{userName.name}</div>
              </div>
            </div>

            <div className="col-sm-4">
              <label htmlFor="Selectrole">Төрөл</label>
              <select className="form-control" id="cateSelect" name="cate" defaultValue={cate} >
                <option value="">Сонгох</option><option value="1">Аялал</option><option value="2">Мэдээлэл</option><option value="3">Блог</option>
              </select>
            </div>
            <div className="col-sm-4">
              <label htmlFor="Selectrole">Төрөл</label>
              <select className="form-control" id="cateIdSelectss" name="cateId" defaultValue={cateId}>
                <option value="">Сонгох</option>
              </select>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
