import React, { useEffect,useState, useContext, Fragment } from 'react';
import { useParams }  from "react-router-dom";
//import axios from '../Axios';
import Spinner from '../spinner';
//import UserContext from '../../context/UserContext';
import VacationContext from '../../context/VacationContext';
import DraggableList from '../drag/DraggableList';

export default function FormVacation() {
  //const userCtx = useContext(UserContext);
  const vacationCtx = useContext(VacationContext);
  //const navigate = useNavigate();
  let { id } = useParams();
  const vacationInachal = {
    loading:false,
    cCode: null,
    consultant: null,
    name: null,
    itinerary: null,
    content: null,
    day: [8,7],
    price: [],
    program: [],
    abstract: [],
    priceIncluded: [["safdasfasf","asfasfasdfsssssssss"],["dddd","qqqqqqqqqqqqqqq"]],
    error:null,
  }
  const [state, setState] = useState(vacationInachal);
  useEffect(()=>{
    if(id){
      setState(vacationInachal);
    }
  },[id]);
  const dayfun = (day) =>{
    const dayarr = [];
    for(var i = 1; i <=day; i++) {
      var obj = {}; obj['value'] = i;
      dayarr.push(obj);
    }
    return dayarr;
  }
  const days = [];
  for(var i = 1; i <=50; i++) {
    var obj = {}; obj['value'] = i+" Өдөр"; obj['label'] = i+" Өдөр";
    days.push(obj);
  }
  const handleType = (e) =>{
    const {name, value} = e.target;
    setState((stateBefore)=>({...stateBefore, [name]:value, error:null}));
  }
  return (
    <div className="col-lg-12">
    {state.loading ? <Spinner /> :
    <div className="card card-primary">
      <div className="card-header">
        <h3 className="card-title"><span style={{color:'red', marginRight:5}}></span>Аяллын мэдээллийг үнэн зөв, бүрэн бөглөнө үү.</h3>
      </div>
      <div className="card-body">
        <div className="row">
          <div className="col-sm-8">
          <label htmlFor="Selectrole">Аяллын нэр</label>
          <input type="text" className="form-control" name="name" placeholder="Аяллын нэр" defaultValue={state.name} onChange={handleType}/>
          </div>
          <div className="col-sm-2">
          <label htmlFor="Selectrole">Өдөр {state.day[0]}</label>
            <select className="form-control" id="roleSelectss" name="role" defaultValue={state.day[0]} >
              <option value="">Сонгох</option>
            </select>
          </div>
          <div className="col-sm-2">
          <label htmlFor="Selectrole">Шөнө {state.day[1]}</label>
            <select className="form-control" id="roleSelectss" name="role" >
              <option value="">Сонгох</option>
            </select>
          </div>
        </div>
        <div className="row">
          <div className="col-sm-12">
            <label htmlFor="Selectcoin">Чиглэл</label>
            <input type="text" className="form-control" name="itinerary" placeholder="Чиглэл" defaultValue={state.itinerary} onChange={handleType}/>
          </div>
        </div>
        <div className="row">
          <div className="col-sm-12">
            <label htmlFor="Selectrole">Аяллын онцлог</label>
            <textarea className="form-control" rows={3} placeholder="Аяллын онцлог" name="content" defaultValue={state.content} onChange={handleType} />
          </div>
        </div>
        <div className="row">
          <div className="col-12">
            <div className="card">
              <div className="card-header d-flex p-0">
              <label htmlFor="Selectrole">Аяллын хөтөлбөр</label>
                <ul className="nav nav-pills ml-auto p-2">
                {state.day[0] && dayfun(state.day[0]).map((option,index) => (
                  <Fragment key={index}>
                  <li className="nav-item">
                    <a className={`nav-link ${option.value === 1 && 'active'}`} href={`#tab_${option.value}`} data-toggle="tab">{option.value}-р өдөр</a>
                  </li>
                  </Fragment>
                ))}
                </ul>
              </div>
              <div className="card-body">
                <div className="tab-content">
                {state.day[0] && dayfun(state.day[0]).map((option,index) => (
                  <div key={index} className={`tab-pane ${option.value === 1 && 'active'}`} id={`tab_${option.value}`}>
                  <div style={{display:'flex', flexDirection:'row'}}>
                    <div className="custom-control custom-checkbox">
                      <input className="custom-control-input" type="checkbox" id={`${index}customCheckbox1`} />
                      <label htmlFor={`${index}customCheckbox1`} className="custom-control-label">Өглөөний цай</label>
                    </div>
                    <div className="custom-control custom-checkbox" style={{marginLeft:30}}>
                      <input className="custom-control-input" type="checkbox" id={`${index}customCheckbox2`} />
                      <label htmlFor={`${index}customCheckbox2`} className="custom-control-label">Өдрийн хоол</label>
                    </div>
                    <div className="custom-control custom-checkbox" style={{marginLeft:30}}>
                      <input className="custom-control-input" type="checkbox" id={`${index}customCheckbox3`} />
                      <label htmlFor={`${index}customCheckbox3`} className="custom-control-label">Оройн хоол</label>
                    </div>
                  </div>
                  <textarea className="form-control" rows={5} placeholder={`${option.value}-р өдөр`} defaultValue={""} />
                </div>
                ))}

                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="row">
          <div className="col-sm-6">
            <label htmlFor="Selectrole">Аяллын үнэд багтсан зүйлс</label>
            <div className="input-group input-group-sm">
              <input type="text" className="form-control" />
              <span className="input-group-append">
                <button type="button" className="btn btn-info btn-flat">Нэмэх</button>
              </span>
            </div>
            <DraggableList data={state.priceIncluded[0]} priceIncluded={setState} vacationState={(1,state)} priceIncludedCate={1}/>
          </div>
          <div className="col-sm-6">
            <label htmlFor="Selectrole">Аяллын үнэд багтаагүй зүйлс</label>
            <div className="input-group input-group-sm">
              <input type="text" className="form-control" />
              <span className="input-group-append">
                <button type="button" className="btn btn-info btn-flat">Нэмэх</button>
              </span>
            </div>
            <DraggableList data={state.priceIncluded[1]} priceIncluded={setState} vacationState={(1,state)} priceIncludedCate={2}/>
          </div>
        </div>
      </div>
    </div>
    }
  </div>
  )
}
