import React, { useContext, useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom';
import axios from '../../components/Axios';
import UserContext from '../../context/UserContext';
import VacationContext from '../../context/VacationContext';
export default function VacationPrice() {
  let { id } = useParams();
  const vacationCtx = useContext(VacationContext);
  const userCtx = useContext(UserContext);
  const [vdate, setVdate] = useState({name:null,days:[],error:null});
  useEffect(()=>{
    if(id){
        axios.get("vacations/"+id).then((result)=>{
          const resultData = result.data.data;
          setVdate({
            name:resultData.name,
            days:resultData.timetable,
            error:null,
          })
        }).catch((err)=>{
          setVdate({name:null,days:[],error:err})
        })
    }
  },[]);
  return (
    <div className="content-wrapper">
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-10">
              <h1 className="m-0">Үнэ засварлах: {vacationCtx.vacationState.mcode} кодтой {vdate.name}</h1>
            </div>{/* /.col */}
            <div className="col-sm-2">
              <Link to={`../vacations/${vacationCtx.vacationState.consultantid}`}>
                <button type="button" className="btn btn-block btn-primary btn-sm">Буцах</button>
              </Link>
            </div>
          </div>{/* /.row */}
        </div>{/* /.container-fluid */}
      </div> 
      <div className="content">
        <div className="container-fluid">
          <div className="row">

            <div className="col-lg-12">
              <div className="card card-primary">
                <div className="card-header">
                  <h3 className="card-title">Аяллын үнэ</h3>
                </div>
                <div className="card-body">
                  <div className="row">
                    <div className="col-sm-6">
                      <input type="text" className={`form-control ${vdate.name ? "" : " is-invalid"}`} name="name" placeholder="Үнийн тайлбар" defaultValue={vdate.name} />
                    </div>
                  </div>
                  <div className="row">
                    <div className="col-sm-1" style={{marginLeft:30, marginTop:10}}>
                      <input type="text" className={`form-control ${vdate.name ? "" : " is-invalid"}`} name="name" placeholder="Үнийн тайлбар" defaultValue={vdate.name} />
                    </div>
                  </div>
                  <div className="row">
                    <div className="col-sm-2">
                      <label htmlFor="Selectrole">Үнэ</label>
                      <ul>
                       
                      </ul>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            
          </div>
        </div>
      </div>
    </div>
  )
}
