import React, { useEffect,useState,useRef, useContext } from 'react';
import { useParams, useNavigate }  from "react-router-dom";
import axios from '../Axios';
import Spinner from '../spinner';
import UserContext from '../../context/UserContext';
import VacationContext from '../../context/VacationContext';

export default function Vacationform() {
  const userCtx = useContext(UserContext);
  const vacationCtx = useContext(VacationContext);
  const navigate = useNavigate();
  let { id } = useParams();
  const vacationInachal = {
    loading:false,
    cCode: null,
    consultant: null,
    name: null,
    itinerary: null,
    itineraryId: null,
    content: null,
    star: null,
    day: [],
    timetable: [],
    price: [],
    program: [],
    abstract: [],
    priceIncluded: [],
    error:null,
  }
  const [state, setState] = useState(vacationInachal);
  useEffect(()=>{

  },[id]);
  return (
    <div className="col-lg-7">
    {state.loading ? <Spinner /> :
    <div className="card card-primary">
      <div className="card-header">
        <h3 className="card-title"><span style={{color:'red', marginRight:5}}></span>{vacationCtx.vacationState.mcode}Үнэн зөв, бүрэн бөглөнө үү.</h3>
      </div>
      <div className="card-body">
        <div className="row">
          <div className="col-sm-6">
          <label htmlFor="Selectrole">Role</label>
            
          </div>
          <div className="col-sm-6">
            <label htmlFor="Selectcoin">Coin:</label>
            <input type="number" className="form-control" name="coin" placeholder="Coin"/>
          </div>
        </div>
      </div>

    </div>
    }
  </div>
  )
}
