import React, { useContext, useState } from 'react'
import { Link, useParams } from 'react-router-dom'
import UserContext from '../../context/UserContext';
import CommentDataTable from '../comment/CommentDataTable';

export default function NewsComment() {
  const { id } = useParams();
  const userCtx = useContext(UserContext);
  const [form, setForm] = useState({nid:null, ac:false});
  const [error, setError] = useState(null);
  return (
    <div className="content-wrapper">
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-10">
              <h1 className="m-0">Мэдээллийн сэтгэгдэл</h1>
            </div>{/* /.col */}
            <div className="col-sm-2">
              <Link to={`../news`}>
                <button type="button" className="btn btn-block btn-primary btn-sm">Буцах</button>
              </Link>
            </div>
          </div>{/* /.row */}
        </div>{/* /.container-fluid */}
      </div> 
      <div className="content">
        <div className="container-fluid">
        {error && <div style={{fontSize:18, color:'red'}}><strong>Алдаа гарлаа!!! {error}</strong></div>}
          <div className="row">
            <CommentDataTable cate={2} cateId={id}/>
          </div>
        </div>
      </div>
    </div>
  )
}
