import React, { useContext, useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'
import BlogListTable from '../../components/blog/BlogListTable'
import axios from '../../components/Axios';
import UserContext from '../../context/UserContext';
import BlogForm from '../../components/blog/BlogForm';
export default function blogList() {
  let { id } = useParams();
  const userCtx = useContext(UserContext);
  const [name, setName] = useState(null);
  const [error, setError] = useState(null);
  const [form, setForm] = useState(false);
  useEffect(()=>{
    if(id){
      axios.get('users/'+id+"?select=lastName firstName",{
        headers:{Authorization:`Bearer ${userCtx.userState.token}`}
      }).then((result)=>{
        setName(result.data.data.firstName);
      }).catch((err)=>{
        err.response.data.error ? setError(err.response.data.error.MNMessage) : setError("Алдаа.");
      })
    }
  },[])
  const formSet = ()=>{
    setForm(true);
  }
  return (
    form ? 
    <BlogForm setForm={setForm}/>
    :
    <div className="content-wrapper">
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-8"><h1 className="m-0">/{name}/ Блог</h1></div>
            <div className="col-sm-2">
              <button type="button" className="btn btn-block btn-success" onClick={formSet}>Блог нэмэх</button>
            </div>
            <div className="col-sm-2">
              <Link to="../blog"><button type="button" className="btn btn-block btn-primary">Буцах</button></Link>
            </div>
          </div>
        </div>
      </div> 
      <div className="content">
        <div className="container-fluid">
          <div className="row">
            <BlogListTable uid={id} uname={name}/>
          </div>
        </div>
      </div>
    </div>
  )
}
