import React,{ useState } from 'react'
import PropTypes from 'prop-types'
import './draggable-list.css'
import DraggableListItem from './DraggableListItem'
function DraggableList(props) {
    console.log(props.data)
    const [data, setData] = useState(props.data);
    const [dragStartIndex, setDragStartIndex] = useState(null)
    const onDragStart = (index) => setDragStartIndex(index)
  return (
    <ul className='draggable-list'>
        { data &&
            data.map((item, index) =>(
                <DraggableListItem 
                    key={index} index={index} onDragStart={(index) => onDragStart(index)}
                >{item}</DraggableListItem>
            ))
        }
        <DraggableListItem key={data.length} index={index} onDragStart={(index) => onDragStart(index)} draggale={false}/>
    </ul>
  )
}

DraggableList.propTypes = {
    data: PropTypes.array
}

export default DraggableList
