import React, { useState } from 'react';
import axios from 'axios';

const UserContext = React.createContext();

const initialState = {
    saving: false,
    logginIn: false,
    error: null,
    errorCode:null,
    token: null,
    userId: null
}

export const UserStore = (props) =>{
    const [state, setState] = useState(initialState);
    const loginUser = (email, password) =>{
        setState({...state, saving:true });
        //const data = {email,password,returnToken:true};
        axios.post('http://localhost:8000/api/users/login',{
        email: email,
        password: password
        }).then((result) => {
            const resultData = result.data.data;
            setState({...state, saving:false,token:resultData.token,userId:resultData._id,error:null});
        }).catch((err) => {
            setState({...state, saving:false,token:null,userId:null, error:err.response.data.error.MNMessage});
            console.log(state.error)
        });
    }
    return(
        <UserContext.Provider value={{ state, loginUser }}>
            {props.children}
        </UserContext.Provider>
    );
}

export default UserContext;
