import React, { useContext, useEffect, useRef, useState } from 'react'
import { Link, useParams } from 'react-router-dom';
import axios from '../../components/Axios';
import UserContext from '../../context/UserContext';
import VacationContext from '../../context/VacationContext';
export default function About() {
  let { id } = useParams();
  const vacationCtx = useContext(VacationContext);
  const userCtx = useContext(UserContext);
  const [hostImageUrl] = useState("http://localhost:8000/photos/");

  const [aboutImage, setAboutImage] = useState(null);
  const [onAboutImage, setOnAboutImage] = useState(hostImageUrl+"no-image.png");
  const [aboutImageProgress, setAboutImageProgress] = useState(null);
  const [aboutVideo, setAboutVideo] = useState(null);
  const [onAboutVideo, setOnAboutVideo] = useState(hostImageUrl+"no-video.png");
  const [aboutVideoProgress, setAboutVideoProgress] = useState(null);

  const [about, setAbout] = useState({title:"", bodytext:""});

  const [aboutError, setAboutError] = useState(null);
  useEffect(()=>{
    if(id){
      
    }
  },[]);


  const imageRef = useRef();
  const imageAdd = ()=>{
   
  }
  const videoRef = useRef();
  const videoAdd = ()=>{
   
  }

  const onSelectImage = (e)=>{
    setAboutError(null);
    const selectedFiles = e.target.files;
    const selectedFilesArray = Array.from(selectedFiles);
    let imagesArray = "";
    if(selectedFilesArray[0].size < 5000000){imagesArray=URL.createObjectURL(selectedFilesArray[0]);}
    if(imagesArray){setOnAboutImage(imagesArray)}
  }

  const onSelectVideo = ()=>{
    setAboutError(null);
  }
  const handleType = (e)=>{
    setAboutError(null);
    const {name, value} = e.target;
    setAbout((stateBefore)=>({...stateBefore, [name]:value,}));
  }
  const createHandle = ()=>{

  }


  return (
    <div className="content-wrapper">
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-10">
              <h1 className="m-0">Бидний тухай</h1>
              {aboutError && <div style={{fontSize:18, color:'red'}}><strong>Алдаа гарлаа!!! {aboutError}</strong></div>}
            </div>
          </div>{/* /.row */}
        </div>{/* /.container-fluid */}
      </div>
      <div className="content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-lg-6">
              <div className="card card-primary">
                <div className="card-header">
                  <h3 className="card-title">Хамт олны зураг</h3>
                </div>
                <div className="card-body">
                  <div className="row">
                    <div className="col-sm-10">
                      <section>
                        <label 
                          style={{ 
                            display:'flex',
                            justifyContent:'center',
                            alignItems:'center',
                            border:'1px dotted black',
                            borderRadius:'5px',
                            cursor:'pointer',
                            height:'40px'
                          }}
                        >Зураг сонгох: 4MB дээш зураг сонгогдохгүй!
                          <input type="file" ref={imageRef} name="image" style={{display:'none'}} 
                            onChange={onSelectImage} accept='image/jpg, image/jpeg, image/png'
                          />
                        </label>
                      </section>
                    </div>
                    {onAboutImage !== hostImageUrl+"no-image.png" && 
                      <div className="col-sm-2">
                        <button type="button" className="btn btn-info btn-flat" onClick={imageAdd} 
                          style={{height:'40px', borderRadius:'5px'}}>Хадгалах
                        </button>
                      </div>
                    }
                    
                  </div>
                  <div className="row" style={{marginTop:15}}>
                    {aboutImageProgress > 0 ?
                    <>
                      <div className="col-sm-5">
                        <div className="progress">
                          <div className="progress-bar bg-primary progress-bar-striped" role="progressbar" style={{width: `${aboutImageProgress}%`}}>
                          </div>
                        </div>
                      </div>
                      <div className="col-sm-1">
                        <div style={{marginTop:-5, textAlign:'left'}}>{aboutImageProgress}%</div>
                      </div>
                    </>
                    : onAboutImage  &&
                      <div className="col-sm-6">
                        <img src={onAboutImage} className="img-fluid mb-12" alt="white sample" />
                      </div>
                    }
                    {aboutImage &&
                      <div className="col-sm-6">
                        <img src={`${hostImageUrl}vacationcover/${aboutImage}`} className="img-fluid mb-12" alt="white sample" />
                      </div>
                    }
                  </div>
                </div>
              </div>
            </div>
            <div className="col-lg-6">
              <div className="card card-primary">
                <div className="card-header">
                  <h3 className="card-title">Танилцуулга бичлэг</h3>
                </div>
                <div className="card-body">
                  <div className="row">
                    <div className="col-sm-10">
                      <section>
                        <label 
                          style={{ 
                            display:'flex',
                            justifyContent:'center',
                            alignItems:'center',
                            border:'1px dotted black',
                            borderRadius:'5px',
                            cursor:'pointer',
                            height:'40px'
                          }}
                        >Бичлэг сонгох: 10MB дээш бол сонгогдохгүй! зөвхөн mp4 файл боломжтой
                          <input type="file" ref={videoRef} name="video" style={{display:'none'}} 
                            onChange={onSelectVideo} accept='video/mp4'
                          />
                        </label>
                      </section>
                    </div>
                    {onAboutVideo !== hostImageUrl+"no-image.png" && 
                      <div className="col-sm-2">
                        <button type="button" className="btn btn-info btn-flat" onClick={videoAdd} 
                          style={{height:'40px', borderRadius:'5px'}}>Хадгалах
                        </button>
                      </div>
                    }
                    
                  </div>
                  <div className="row" style={{marginTop:15}}>
                    {aboutVideoProgress > 0 ?
                    <>
                      <div className="col-sm-5">
                        <div className="progress">
                          <div className="progress-bar bg-primary progress-bar-striped" role="progressbar" style={{width: `${aboutVideoProgress}%`}}>
                          </div>
                        </div>
                      </div>
                      <div className="col-sm-1">
                        <div style={{marginTop:-5, textAlign:'left'}}>{aboutVideoProgress}%</div>
                      </div>
                    </>
                    : onAboutVideo  &&
                      <div className="col-sm-6">
                        <img src={onAboutVideo} className="img-fluid mb-12" alt="white sample"/>
                      </div>
                    }
                    {aboutVideo &&
                      <div className="col-sm-6">
                        <img src={`${hostImageUrl}vacationvideo/${aboutVideo}`} className="img-fluid mb-12" alt="white sample" />
                      </div>
                    }
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-12">
              <div className="card card-primary">
                <div className="card-header"><h4 className="card-title">Танилцуулга</h4></div>
                <div className="card-body">

                  <div className="row">
                    <div className="col-sm-12">
                    <label htmlFor="Selectcoin">Гарчиг</label>
                    <input type="text" className={`form-control ${about.title ? "" : " is-invalid"}`} name="title" placeholder="Гарчиг" defaultValue={about.title} onChange={handleType}/>
                    </div>
                  </div>
                  <div className="row">
                    <div className="col-sm-12">
                    <label htmlFor="Selectrole">Танилцуулга</label>
                    <textarea className={`form-control ${about.bodytext ? "" : " is-invalid"}`} rows={8} placeholder="Танилцуулга" name="bodytext" defaultValue={about.bodytext} onChange={handleType} />
                    </div>
                  </div>

                  <div className="card-footer">
                    <button className={`btn btn-primary ${(about.title && about.bodytext) ? "" : " disabled"}`} disabled={!(about.title && about.bodytext)} style={{float:'right'}} onClick={createHandle}>Хадгалах</button>
                  </div>

                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
