    {label:'Булган',value:'bulgan'},{label:'Говь-Алтай',value:'gobialtai'},{label:'Говьсүмбэр',value:'gobisumber'},
    {label:'Дархан-Уул',value:'darhan'},{label:'Дорноговь',value:'dornogobi'},{label:'Дорнод',value:'dornod'},
    {label:'Дундговь',value:'dundgobi'},{label:'Завхан',value:'zavkhan'},{label:'Орхон',value:'orkhon'},
    {label:'Сэлэнгэ',value:'selenge'},{label:'Сүхбаатар',value:'sukhbaatar'},{label:'Төв',value:'tuv'},
    {label:'Увс',value:'uvs'},{label:'Ховд',value:'khovd'},{label:'Хэнтий',value:'khentii'},
    {label:'Хөвсгөл',value:'khuvsgul'},{label:'Өвөрхангай',value:'uvurkhangai'},{label:'Өмнөговь',value:'umnugobi'}
  ];

  const district = ['Хан-Уул','Сүхбаатар','Сонгинохайрхан','Баянзүрх','Баянгол','Чингэлтэй','Налайх','Багануур','Багахангай'];
  const horoo = [];
  for(var i = 1; i <=50; i++) {
    var obj = {}; obj['value'] = i+"-р хороо"; obj['label'] = i+"-р хороо";
    horoo.push(obj);
  }
  const citychange = (e)=>{
    const {name, value} = e.target;
    const districtdiv = document.getElementById("districtdiv"); districtdiv.value="";
    const khoroodiv = document.getElementById("khoroodiv"); khoroodiv.value="";
    const sumdiv = document.getElementById("sumdiv"); sumdiv.value="";
    if(name==="addresscity"&&value==="Улаанбаатар"){
      districtdiv.style.display=""; khoroodiv.style.display=""; sumdiv.style.display="none"; 
      setAimagsum([]);
    }else{
      districtdiv.style.display="none"; khoroodiv.style.display="none";  sumdiv.style.display="";
      const aimagsumar=[]; address[value].map((option) => aimagsumar.push(option))
      setAimagsum(aimagsumar);
    }
    setState({...state, address:[value,state.address[1],state.address[2],state.address[3]], error:null,success:null});
  }
  const addresschange = (e) =>{
    const {name, value} = e.target; let selectedaddress=[];
    if(name==="addressdistrict"){ selectedaddress=[state.address[0],value,state.address[2],state.address[3]] }
    else if(name==="addresskhoroo"){ selectedaddress=[state.address[0],state.address[1],value,state.address[3]] }
    else if(name==="addresssum"){ selectedaddress=[state.address[0],value,state.address[2],state.address[3]] }
    else if(name==="addresstext"){ selectedaddress=[state.address[0],state.address[1],state.address[2],value] }
    setState({...state, address:selectedaddress, error:null,success:null});
  }
  const handleType = (e) =>{
    const {name, value} = e.target;
    if(name==='passportser'){ setState({...state, passport:[value,state.passport[1],state.passport[2]], error:null,success:null}); }
    else if(name==='passportnumber'){ setState({...state, passport:[state.passport[0],value,state.passport[2]], error:null,success:null}); }
    else if(name==='passportend'){ setState({...state, passport:[state.passport[0],state.passport[1],value], error:null,success:null}); }
    else{ setState({...state, [name]:value, error:null,success:null}); }
  }
  return (
  <div className="col-lg-7">
    <div className="card card-primary">
      <div className="card-header">
        <h3 className="card-title">Үнэн зөв, бүрэн бөглөнө үү.</h3>
      </div>
      <form>
        <div className="card-body">
          <div class="row">
            <div class="col-sm-2">
              {state.profileimage 
                ?<img src={state.profileimage} alt="" style={{height:"100px"}}/>
                :null
              }
            </div>
            <div class="col-sm-10">
              <label htmlFor="profileimage">Profile image</label>
              <div className="input-group">
                <div className="custom-file">
                  <input type="file" className="custom-file-input" id="profileimage" />
                  <label className="custom-file-label" htmlFor="profileimage">Profile image</label>
                </div>
              </div>
            </div>
          </div>
          <hr/>
          <div class="row">
            <div class="col-sm-12">
            <label htmlFor="Selectrole">Role</label>
              <select class="form-control" id="Selectrole" value={state.role}>
              {props.role === 'Admin' ? (<><option>Operator</option><option>Sub admin</option><option>Admin</option></>):(<option>User</option>)}
              </select>
            </div>
          </div>
          <div class="row">
            <div class="col-sm-6">
              <label htmlFor="Inputlastname">Last Name</label>
              <input type="text" className="form-control" name="lastname" placeholder="Last Name" value={state.lastname} onChange={handleType}/>
            </div>
            <div class="col-sm-6">
              <label htmlFor="Inputfirstname">First Name</label>
              <input type="text" className="form-control" name="firstname" placeholder="First Name" value={state.firstname} onChange={handleType}/>
            </div>
          </div>
          <div class="row">
            <div class="col-sm-6">
              <label htmlFor="Inputemail">Email</label>
              <input type="email" className="form-control" name="email" placeholder="Email" value={state.email} onChange={handleType}/>
            </div>
            <div class="col-sm-6">
              <label htmlFor="Inputphone">Phone number</label>
              <input type="phone" className="form-control" name="phone" placeholder="Phone number" value={state.phone} onChange={handleType}/>
            </div>
          </div>
          <div class="row">
            <div class="col-sm-6">
              <label htmlFor="Selectgender">Gender</label>
              <select class="form-control" name="gender" value={state.gender} onChange={handleType}>
                <option>Select</option>
                <option>Male</option>
                <option>Female</option>
              </select>
            </div>
            <div class="col-sm-6">
              <label htmlFor="Inputbirthdate">Birthdate</label>
              <input type="date" className="form-control" name="birthdate" placeholder="Birthdate" value={state.birthdate} onChange={handleType}/>
            </div>
          </div>
          <div class="row">
            <div class="col-sm-2">
              <label htmlFor="Selectser">Ser</label>
              <select class="form-control" name="passportser" value={state.passport[0]} onChange={handleType}>
                <option>E</option>
                <option>A</option>
                <option>D</option>
                <option>G</option>
                <option>EC</option>
                <option>NULL</option>
              </select>
            </div>
            <div class="col-sm-4">
              <label htmlFor="Inputpassportnumber">Passport number</label>
              <input type="number" className="form-control" name="passportnumber" placeholder="Passport number" value={state.passport[1]} onChange={handleType}/>
            </div>
            <div class="col-sm-6">
              <label htmlFor="Inputpassportend">Passport end</label>
              <input type="date" className="form-control" name="passportend" placeholder="Phone end" value={state.passport[2]} onChange={handleType}/>
            </div>
          </div>
          <div class="row">
            <div class="col-sm-2">
              <label htmlFor="Selectcity">City</label>
              <select class="form-control" name="addresscity" onChange={citychange} value={state.address[0]}>
              <option value="Улаанбаатар">Улаанбаатар</option>
                {city.map((option) => (
                  <option value={option.value}>{option.label}</option>
                ))}
              </select>
            </div>
            <div id="districtdiv" class="col-sm-2">
              <label htmlFor="Selectdistrict">District</label>
              <select class="form-control" id="Selectdistrict" name="addressdistrict" onChange={addresschange} value={state.address[1]}>
                <option>Select</option>
                {district.map((option) => (
                  <option value={option}>{option}</option>
                ))}
              </select>
            </div>
            <div id="khoroodiv" class="col-sm-2">
              <label htmlFor="Selectkhoroo">Khoroo</label>
              <select class="form-control" id="Selectkhoroo" name="addresskhoroo" onChange={addresschange} value={state.address[2]}>
                <option>Select</option>
                {horoo.map((option) => (
                  <option value={option.value}>{option.label}</option>
                ))}
              </select>
            </div>
            <div id="sumdiv" class="col-sm-4" style={{display:'none'}}>
              <label htmlFor="Inputsum">Sum</label>
              <select class="form-control" id="Selectsum" name="addresssum" onChange={addresschange} value={state.address[1]}>
                <option value="">Select</option>
                {aimagsum.map((option) => (
                  <option value={option}>{option}</option>
                ))}
              </select>
            </div>
            <div class="col-sm-6">
              <label htmlFor="Inputaddress">Address</label>
              <input type="text" className="form-control" name="addresstext" placeholder="Address" onChange={addresschange} value={state.address[3]}/>
            </div>
          </div>
        </div>
        {/* /.card-body */}
        <div className="card-footer">
          <button type="submit" className="btn btn-primary">{id ? "Засах":"Нэмэх"}</button>
        </div>
      </form>
    </div>
  </div>
  )
}
