import React, { useContext, useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom';
import axios from '../../components/Axios';
import VacationContext from '../../context/VacationContext';
export default function VacationDate() {
  let { id } = useParams();
  const vacationCtx = useContext(VacationContext);
  const [vdate, setVdate] = useState({name:null,days:[],error:null});
  useEffect(()=>{
    if(id){
        axios.get("vacations/"+id).then((result)=>{
          const resultData = result.data.data;
          setVdate({
            name:resultData.name,
            days:resultData.day,
            error:null,
          })
        }).catch((err)=>{
          console.log(err)
          setVdate({name:null,days:[],error:err})
        })
    }
  },[])
  return (
    <div className="content-wrapper">
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-10">
              <h1 className="m-0">{vacationCtx.vacationState.mcode} кодтой {vdate.name} /Хуваарь/</h1>
            </div>{/* /.col */}
            <div className="col-sm-2">
              <Link to={`../vacations/${vacationCtx.vacationState.consultantid}`}>
                <button type="button" className="btn btn-block btn-primary btn-sm">Буцах</button>
              </Link>
            </div>
          </div>{/* /.row */}
        </div>{/* /.container-fluid */}
      </div> 
      <div className="content">
        <div className="container-fluid">
          <div className="row">

          </div>
        </div>
      </div>
    </div>
  )
}
