import React, { useState } from 'react';
import axios from '../components/Axios';

const UserContext = React.createContext();

const initialState = {
    loading: false,
    userId: null,
    token: null,
    errorCode:null,
    error: null,
}

export const UserStore = (props) =>{
    const [state, setState] = useState(initialState);
    const loginUser = (email, password) =>{
        setState({...state, loading:true });
        //const data = {email,password,returnToken:true};
        axios.post('users/login',{
        email: email,
        password: password
        }).then((result) => {
            const resultData = result.data.data;
            setState({...state, loading:false,token:resultData.token,userId:resultData._id,error:null});
            localStorage.setItem("token",result.data.token);
            
        }).catch((err) => {
            setState({...state, loading:false,token:null,userId:null, error:err.response.data.error.MNMessage});
        });
    }
    const loginErrorClear = () =>{
        setState({...state, loading:false,token:null,userId:null, error:null});
    }

    return(
        <UserContext.Provider value={{ state, loginUser, loginErrorClear }}>
            {props.children}
        </UserContext.Provider>
    );
}

export default UserContext;
