import React, { useContext, useEffect, useState } from 'react'
import { Link }  from "react-router-dom";
import Spinner from '../spinner'
import UserContext from '../../context/UserContext';
import axios from '../Axios';
  export default function DataTable(props) { 
    const userCtx = useContext(UserContext);
    const initialState = {
      loading: false,
      data:[],
      errorCode:null,
      error: null,
      editUrl: props.cate === '1' ? 'adminform' : props.cate === '2' && 'userform',
    }
    const [usersState, setUsersState] = useState(initialState);
    useEffect(()=>{

      const script = document.createElement('script');
      script.src = 'js/init_datatable.js';
      script.async = true;
      document.body.appendChild(script);
      let roleSort = "";
      if(props.cate === '1'){roleSort="?role=admin&role=manager&role=consultant"}else if(props.cate === '2'){roleSort="?role=user&role=blog"}
        setUsersState({...usersState, loading:true});
        axios.get(`users${roleSort}`,{
          headers:{Authorization:`Bearer ${userCtx.userState.token}`}
        }).then((result)=>{
          setUsersState({
            loading: false,
            data:result.data.data,
            errorCode:null,
            error: null,
            editUrl: usersState.editUrl,
          })
        }).catch((err)=>{
          setUsersState({
            loading: false,
            data:null,
            errorCode:err.response.data.error.statusCode,
            error: err.response.data.error.MNMessage,
            editUrl: usersState.editUrl,
          })
        })      
    },[]);
    return (
    <div className="col-lg-12">
      <div className="card">
        <div className="card-body">
        {usersState.loading ? <Spinner /> :
          <table id="example1" className="table table-bordered table-striped">
            <thead>
              <tr>
                <th>№</th>
                <th>Role</th>
                <th>Email</th>
                <th>Lastname</th>
                <th>Firstname</th>
                <th>Phone</th>
                <th>Edit</th>
                <th>Password</th>
              </tr>
            </thead>
            <tbody>
            <tr style={{display:"none"}}><td></td><td></td><td></td><td></td><td></td><td></td><td></td></tr>
           {usersState.data && usersState.data.map((option,index) => (
                  <tr key={index}>
                    <td>{index+1}</td>
                    <td>{option.role}</td>
                    <td>{option.email}</td>
                    <td>{option.lastName}</td>
                    <td>{option.firstName}</td>
                    <td>{option.phone}</td>
                    <td>
                      <Link to={`../${usersState.editUrl}/${option._id}`}>
                        <button type="button" className="btn btn-block btn-primary btn-sm">Edit</button>
                      </Link>
                    </td>
                    <td>
                      <Link to={`../userpassword/${option._id}`}>
                        <button type="button" className="btn btn-block btn-warning btn-sm">Password</button>
                      </Link>
                    </td>
                  </tr>
            ))}
            </tbody>
          </table>
        }
        </div>
      </div>
    </div>
    )
}
