import React, { useState } from 'react';
import axios from '../components/Axios';

const UserContext = React.createContext();
const initialState = {
    loading: false,
    userId: null,
    token: null,
    errorCode:null,
    error: null,
}

export const UserStore = (props) =>{
    const [userSstate, setUserState] = useState(initialState);
    const loginUser = (email, password) =>{
        setUserState({...userSstate, loading:true });
        //const data = {email,password,returnToken:true};
        axios.post('users/login',{
        email: email,
        password: password
        }).then((result) => {
            const resultData = result.data.data;
            setUserState({...userSstate, loading:false,token:resultData.token,userId:resultData._id,error:null});
            localStorage.setItem("token",result.data.token);
            localStorage.setItem("userId",resultData._id);
        }).catch((err) => {
            setUserState({...userSstate, loading:false,token:null,userId:null, error:err.response.data.error.MNMessage});
        });
    }
    const loginErrorClear = () =>{
        setUserState(initialState);
    }
    const logout = () =>{
        setUserState(initialState);
        localStorage.removeItem("token");
        localStorage.removeItem("userId");
    }
    const loginSucess = (uToken,uId)=>{

        setUserState({...userSstate, loading:false,token:uToken,userId:uId,error:null});
    }

    return(
        <UserContext.Provider value={{ userSstate, loginUser, loginErrorClear, logout, loginSucess }}>
            {props.children}
        </UserContext.Provider>
    );
}

export default UserContext;
