  let { id } = useParams();
  const vacationCtx = useContext(VacationContext);
  const userCtx = useContext(UserContext);
  const [vprovide, setVprovide] = useState({name:null,provide:[],error:null});
  useEffect(()=>{
    const script = document.createElement('script');
      script.src = 'js/init_imageview.js';
      script.async = true;
      document.body.appendChild(script);
    if(id){
      axios.get("vacations/"+id).then((result)=>{
        const resultData = result.data.data;
        setVprovide({ name:resultData.name, provide:resultData.provide, error:null, })
      }).catch((err)=>{
        setVprovide({name:null,provide:[],error:err})
      })
    }
  },[]);

  const savehandle =(newProvide)=>{
