import React, { Component } from 'react'
import axios from 'axios';
export default class Login extends Component {
  state = {
    email:null,
    password:null,
    error:null,
    loading:false,
  }
  handleType = (e) =>{
    const {name, value} = e.target;
    this.setState({[name]:value});
  }
  handleSubmit = () =>{

    axios.post('http://localhost:8000/api/users/login',{
        email: this.state.email,
        password: this.state.password
    }).then(result => console.log(result)).catch(err =>console.log(err))
  }
  render() {
    return (
    <div className="container">
      <div className="card card-info ">
        <div className="card-header">
          <h3 className="card-title">Нэвтрэх хэсэг{this.state.password}</h3>
        </div>
          <div className="card-body">
            <div className="form-group row">
              <label htmlFor="inputEmail3" className="col-sm-2 col-form-label">И-МЭЙЛ</label>
              <div className="col-sm-10">
                <input type="email" className="form-control" name="email" placeholder="И-МЭЙЛ" onChange={this.handleType}/>
              </div>
            </div>
            <div className="form-group row">
              <label htmlFor="inputPassword3" className="col-sm-2 col-form-label">НУУЦ ҮГ</label>
              <div className="col-sm-10">
                <input type="password" className="form-control" name="passowrd" placeholder="НУУЦ ҮГ" />
              </div>
            </div>
          </div>
          <div className="card-footer">
            <button className="button is-link" onClick={this.handleSubmit}>Нэвтрэх</button>
          </div>
      </div>
    </div>
    )
  }
}
