import React, { useContext, useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom';
import axios from '../components/Axios'
import Spinner from '../components/spinner';
import UserContext from '../context/UserContext';
export default function UserPassword() {
    const userCtx = useContext(UserContext);
    let { id } = useParams();
    const [state, setState] = useState({loading:false,email:null,code:null,error:null});
    const [newPass, setNewPass] = useState({loading:false,passOne:null,passTwo:null,resetCode:null,error:null,success:null});
    useEffect(()=>{
        if(id){
            setState({loading:true,email:null,code:null,error:null});
            axios.get('users/'+id,{
              headers:{Authorization:`Bearer ${userCtx.userState.token}`}
            }).then((result)=>{
              setState({loading:false,email:result.data.data.email,code:null,error:null});
            }).catch((err)=>{
              setState({loading:false,email:null,code:null,error:err.response.data.error.MNMessage});
            });
        }
    },[]);
    const codecreate = () =>{
        if(state.email){
            setState({loading:true,email:state.email,code:null,error:null});
            axios.post('users/forgot-password',{email:state.email}).then((result)=>{
                console.log(result);
                setState({loading:false,email:state.email,code:result.data.resetPasswordToken,error:null})
            }).catch((err)=>{console.log(err)})
        }
    }
    const createPass = () =>{
        if(newPass.resetCode){
            if(newPass.passOne && newPass.passTwo){
                if(newPass.passOne === newPass.passTwo){
                    setNewPass((stateBefore)=>({...stateBefore, loading:true,error:null}));
                    axios.post('users/reset-password',{email:state.email,reset_code:newPass.resetCode,password:newPass.passOne})
                    .then((result)=>{
                        setNewPass((stateBefore)=>({...stateBefore, loading:false,error:null,success:"Нууц үг амжиттай солигдлоо."}));
                    }).catch((err)=>{
                        setNewPass((stateBefore)=>({...stateBefore, loading:false,error:err.response.data.error.MNMessage,success:null}));
                    });
                }else{setNewPass((stateBefore)=>({...stateBefore, loading:false,error:"Шинэ нууц үг буруу байна.",success:null}))}
            }else{setNewPass((stateBefore)=>({...stateBefore, loading:false,error:"Шинэ нууц үгээ оруулна уу.",success:null}))}
        }else{setNewPass((stateBefore)=>({...stateBefore, loading:false,error:"Нууц үг шинэчлэх код байхгүй байна.",success:null}))}       
    }
    const handleType = (e) =>{
        const {name, value} = e.target;
        setNewPass((stateBefore)=>({...stateBefore, [name]:value, error:null}));
      }
  return (
    <div className="content-wrapper">
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-5">
              <h1>Нууц үг солих</h1>
            </div>{/* /.col */}
            <div className="col-sm-2">
              <Link to="../users"><button type="button" className="btn btn-block btn-primary btn-sm">Буцах</button></Link>
            </div>
          </div>{/* /.row */}
        </div>{/* /.container-fluid */}
      </div> 
      <div className="content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-lg-7">
              {state.loading ? <Spinner /> :
              <>
                <div className="card card-primary">
                  <div className="card-header">
                    <h3 className="card-title">
                      <span style={{color:'red', marginRight:5}}>{state.error}</span> 
                      Нууц үг шинэчлэх код: {state.code}
                    </h3>
                  </div>
                  <div className="card-body">
                    <div className="row">
                      <div className="col-sm-6">
                        <input type="email" className="form-control" disabled={true} placeholder="email" defaultValue={state.email}/>
                      </div>
                      <div className="col-sm-6">
                        <button className="btn btn-primary" style={{float:'left'}} onClick={codecreate}>Нууц үг шинэчлэх код авах</button>
                      </div>
                    </div>
                  </div>
                </div>

                <div className="card card-primary">
                  <div className="card-header">
                    <h3 className="card-title"><span style={{color:'red', marginRight:5}}>{newPass.error}</span>Нууц үг шинэчлэх</h3>
                  </div>
                  <div className="card-body">
                      <div className="row">
                          <div className="col-sm-6">
                            <label htmlFor="profileimage">Нууц үг шинэчлэх кодоо оруулна уу</label>
                            <input type="number" className="form-control" name="resetCode" placeholder="code" onChange={handleType}/>
                            <label htmlFor="profileimage">Шинэ нууц үгээ оруулна уу</label>
                            <input type="password" className="form-control" name="passOne" onChange={handleType}/>
                            <label htmlFor="profileimage">Шинэ нууц үгээ давтан оруулна уу</label>
                            <input type="password" className="form-control" name="passTwo" onChange={handleType}/>
                            <button className="btn btn-primary" style={{float:'right',marginTop:20}} onClick={createPass}>Нууц үг шинэчлэх</button>
                          </div>
                      </div>
                  </div>
                </div>
              </>
              }
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
