import React, { useContext, useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import axios from '../Axios';
import UserContext from '../../context/UserContext';
import Spinner from '../spinner';
export default function CommentDataTable() {
  const userCtx = useContext(UserContext);
  const [loading, setLoading] = useState(false);
  const [comment, setComment] = useState([]);
  const [error, setError] = useState(null);
  useEffect(()=>{
    setLoading(true);
    axios.get('comment').then((result)=>{setLoading(false); setComment(result.data.data)}).catch((err)=>{setLoading(false); console.log(err)})
  },[])

  const padTo2Digits =(num)=> {
    return num.toString().padStart(2, '0');
  }
  
  const formatDate = (date)=> {
    return (
      [
        date.getFullYear(),
        padTo2Digits(date.getMonth() + 1),
        padTo2Digits(date.getDate()),
      ].join('-') +
      ' ' +
      [
        padTo2Digits(date.getHours()),
        padTo2Digits(date.getMinutes()),
        padTo2Digits(date.getSeconds()),
      ].join(':')
    );
  }

  return (
    <div className="col-lg-12">
      <div className="card">
        <div className="card-body">
        {error && <div style={{fontSize:18, color:'red'}}><strong>Алдаа гарлаа!!! {error}</strong></div>}
        {loading ? <Spinner /> :
          <table id="example1" className="table table-bordered table-striped">
            <thead>
              <tr>
                <th>№</th>
                <th>Activated</th>
                <th>Category</th>
                <th>Name</th>
                <th>Date</th>
                <th>Edit</th>
              </tr>
            </thead>
            <tbody>
              <tr style={{display:"none"}}><td></td><td></td><td></td><td></td><td></td><td></td></tr>
              {comment && comment.map((option,index) =>{
                
                return (
                    <tr key={index}>
                      <td>{index+1}</td>
                      <td>{option.activ}</td>
                      <td>{option.cate}</td>
                      <td>{option.userId}</td>
                      <td>{formatDate(new Date(option.createdAt))}</td>
                      <td>
                        <Link to={`../commentForm/${option._id}`}>
                          <button type="button" className="btn btn-block btn-primary btn-sm">Edit</button>
                        </Link>
                      </td>
                    </tr>
                  )
              }
              )}
            </tbody>
          </table>
        }
        </div>
      </div>
    </div>
  )
}
