import React, { useContext, useState } from 'react'
import { Link, useParams } from 'react-router-dom'
import UserContext from '../../context/UserContext';
import CommentDataTable from '../comment/CommentDataTable';
import CommentForm from '../comment/CommentForm';

export default function NewsComment() {
  const { id } = useParams();
  const userCtx = useContext(UserContext);
  const [form, setForm] = useState({nid:null, ac:false});
  const [error, setError] = useState(null);
  return (
    form.ac ? 
      <CommentForm pcate={2} psetForm={setForm}/>
    :
    <div className="content-wrapper">
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-8">
              <h1 className="m-0">Мэдээллийн сэтгэгдэл</h1>
            </div>
            <div className="col-sm-2">
              <button type="button" className="btn btn-block btn-success" onClick={()=>setForm({bid:null, ac:true})}>Сэтгэгдэл нэмэх</button>
            </div>
            <div className="col-sm-2">
              <Link to={`../news`}>
                <button type="button" className="btn btn-block btn-primary">Буцах</button>
              </Link>
            </div>
          </div>{/* /.row */}
        </div>{/* /.container-fluid */}
      </div> 
      <div className="content">
        <div className="container-fluid">
        {error && <div style={{fontSize:18, color:'red'}}><strong>Алдаа гарлаа!!! {error}</strong></div>}
          <div className="row">
            <CommentDataTable pcate={2} psetForm={setForm}/>
          </div>
        </div>
      </div>
    </div>
  )
}
