import React, { useContext, useEffect, useState } from 'react';
import { Link }  from "react-router-dom";
import Spinner from '../spinner';
import SmallSpinner from '../smallSpinner';
import axios from '../Axios';
import UserContext from '../../context/UserContext';

export default function VacationDataTable(props) {
  const userCtx = useContext(UserContext);
  const initialState = { loading: false, data:[], error: null}
  const [vacation, setVacation] = useState(initialState);
  const [vacationActiv, setVacationActiv] = useState(false);
  const [activLoading, setActivLoading] = useState(false);
  useEffect(()=>{
    setVacation((stateBefore)=>({...stateBefore, loading:true}));
    axios.get(`consultants/${props.mcode}/vacations?select=_id name day timetable price createUser activ`).then((result)=>{
        setVacation({ loading: false, data:result.data.data, error: null})
    }).catch((err)=>{setVacation({ loading: false, data:[], error: "consultants vacations дуудах үед алдаа гарлаа"+err.message })})
  },[props.mcode]);

  const activCheck = (vId,activIf) =>{
    setActivLoading(true);
    axios.put('vacations/'+vId,{activ:!activIf},{
        headers:{Authorization:`Bearer ${userCtx.userState.token}`}
    }).then((result)=>{
      
      vacation.data && vacation.data.map((option,index) => (
        console.log(option)
      ))

      setVacationActiv(!vacationActiv); setActivLoading(false);
    }).catch((err)=>{console.log(err);setActivLoading(false);});
  }
  return (
    <div className="col-lg-12">
    <div className="card">
      <div className="card-body">
      {vacation.loading ? <Spinner /> :
        <table id="example1" className="table table-bordered table-striped">
          <thead>
            <tr>
              <th>№</th>
              <th>Төлөв</th>
              <th>Аяллын нэр</th>
              <th>Хугацаа</th>
              <th>Хуваарь</th>
              <th>Хуулах</th>
              <th>Үнэ</th>
              <th>Засах</th>
            </tr>
          </thead>
          <tbody>
          <tr style={{display:"none"}}><td></td><td></td><td></td><td></td><td></td><td></td><td></td></tr>
         {vacation.data && vacation.data.map((option,index) => (
                <tr key={index}>
                  <td>{index+1}</td>
                  <td>
                    {activLoading ? <SmallSpinner /> :
                      <div 
                        key={index} 
                        className="custom-control custom-switch custom-switch-off-danger custom-switch-on-success" 
                        style={{marginLeft:"5px", textAlign:'center'}}
                      >
                      <input 
                        type="checkbox" className="custom-control-input" id="customSwitch3" 
                        checked={option.activ} onChange={()=>activCheck(option._id,option.activ)}
                      />
                      <label className="custom-control-label" htmlFor="customSwitch3"></label>
                      </div>
                    }
                  </td>
                  <td>{option.name}</td>
                  <td>{option.day[0]} шөнө, {option.day[1]} өдөр</td>
                  <td>{option.timetable[0]}, {option.timetable[1]}</td>
                  <td>Хуулах</td>
                  <td>{ option.price[0] && String(option.price[0][1]).replace(/(.)(?=(\d{3})+$)/g,'$1,')}</td>
                  <td>
                    <Link to={`../vacationform/${option._id}`}>
                      <button type="button" className="btn btn-block btn-warning btn-sm">Засах</button>
                    </Link>
                  </td>
                </tr>
          ))}
          </tbody>
        </table>
      }
      </div>
    </div>
  </div>
  )
}
