import React, { useContext, useEffect, useState } from 'react'
import Spinner from '../spinner';
import axios from '../Axios';
import UserContext from '../../context/UserContext';
export default function VacationDataTable(props) {
  const userCtx = useContext(UserContext);
  const initialState = { loading: false, data:[], error: null, }
  const [vacation, setVacation] = useState(initialState);
  useEffect(()=>{
    axios.get(`consultants/${props.mcode}/vacations?select=_id name day timetable price createUser activ`).then((result)=>{
        console.log(result)
        setVacation({ loading: false, data:result.data.data, error: null, })
    }).catch((err)=>{setVacation({ loading: false, data:[], error: "consultants vacations дуудах үед алдаа гарлаа"+err.message })})
  },[props.mcode]);
  const activCheck = (vId) =>{
console.log("==>",vId)
    // axios.put('vacations/'+vId,{
    //     headers:{Authorization:`Bearer ${userCtx.userState.token}`}
    //   })
  }
  return (
    <div className="col-lg-12">
    <div className="card">
      <div className="card-body">
      {vacation.loading ? <Spinner /> :
        <table id="example1" className="table table-bordered table-striped">
          <thead>
            <tr>
              <th>№</th>
              <th>Төлөв</th>
              <th>Аяллын нэр</th>
              <th>Хугацаа</th>
              <th>Хуваарь</th>
              <th>Хуулах</th>
              <th>Үнэ</th>
              <th>Засах</th>
            </tr>
          </thead>
          <tbody>
          <tr style={{display:"none"}}><td></td><td></td><td></td><td></td><td></td><td></td><td></td></tr>
         {vacation.data && vacation.data.map((option,index) => (
                <tr key={index}>
                  <td>{index+1}</td>
                  <td>
                    <div key={index} className="custom-control custom-switch custom-switch-off-danger custom-switch-on-success" style={{textAlign:'center'}}>
                    <input type="checkbox" className="custom-control-input " checked={option.activ} onChange={activCheck(option._id)}/>
                    <label className="custom-control-label" htmlFor="customSwitch3"></label>
                    </div>
                  </td>
                  <td>{option.name}</td>
                  <td>{option.day[0]} шөнө, {option.day[1]} өдөр</td>
                  <td>{option.timetable[0]}, {option.timetable[1]}</td>
                  <td>Хуулах</td>
                  <td>{String(option.price[0][1]).replace(/(.)(?=(\d{3})+$)/g,'$1,')}</td>
                  <td>{option.activ && "Нээлттэй"}</td>
                </tr>
          ))}
          </tbody>
        </table>
      }
      </div>
    </div>
  </div>
  )
}
