import React, { useContext, useState } from 'react'
import { Link } from 'react-router-dom';
import UserContext from '../../context/UserContext';
import Spinner from '../spinner';
export default function CommentDataTable() {
  const userCtx = useContext(UserContext);
  const [loading, setLoading] = useState(false);
  const [comment, setComment] = useState([]);
  const [error, setError] = useState(null);
  return (
    <div className="col-lg-12">
      <div className="card">
        <div className="card-body">
        {error && <div style={{fontSize:18, color:'red'}}><strong>Алдаа гарлаа!!! {error}</strong></div>}
        {loading ? <Spinner /> :
          <table id="example1" className="table table-bordered table-striped">
            <thead>
              <tr>
                <th>№</th>
                <th>Activated</th>
                <th>Category</th>
                <th>Name</th>
                <th>Date</th>
                <th>Edit</th>
              </tr>
            </thead>
            <tbody>
              <tr style={{display:"none"}}><td></td><td></td><td></td><td></td><td></td><td></td></tr>
              {comment && comment.map((option,index) => (
                <tr key={index}>
                  <td>{index+1}</td>
                  <td>{option.activ}</td>
                  <td>{option.cate}</td>
                  <td>{option.userId}</td>
                  <td>{option.createdAt}</td>
                  <td>
                    <Link to={`../commentForm/${option._id}`}>
                      <button type="button" className="btn btn-block btn-primary btn-sm">Edit</button>
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        }
        </div>
      </div>
    </div>
  )
}
