import React, { useState,useRef } from 'react'

export default function NewsImages() {
    const [onImages, setOnImages] = useState([]);
    const [images, setImages] = useState([]);
    const [error, setError] = useState();
    const [hostImageUrl] = useState("http://localhost:8000/photos/");
    const [imagesProgress, setImagesProgress] = useState();

    const imagesRef = useRef();

    const onSelectImages = () =>{

    }
    const imagesAdd = () =>{

    }
    const deleteImage = () =>{

    }
  return (
    <div>
      <div className="row">
        <div className="col-12">
          <div className="card card-primary">
            <div className="card-header"><h4 className="card-title">Мэдээллийн альбомд зураг нэмэх</h4></div>
            <div className="card-body">
              <div className="row">
                <div className="col-sm-5">
                  <section>
                    <label 
                      style={{ 
                        display:'flex',justifyContent:'center',alignItems:'center',
                        border:'1px dotted black',borderRadius:'5px',cursor:'pointer',height:'40px'
                      }}
                    >Зураг сонгох 3MB дээш зураг сонгогдохгүй!
                      <input type="file" ref={imagesRef} name="coverimage" style={{display:'none'}} multiple
                        onChange={onSelectImages} accept='image/jpg, image/jpeg, image/png'
                      />
                    </label>
                  </section>
                </div>
                <div className="col-sm-5">
                  {onImages.length > 0 && 
                    (onImages.length > 10 ? (<div style={{color:'red'}}>10 дээш зураг нэгэн зэрэг нэмэх боломжгүй. {onImages.length - 10} зургийг устгана уу</div>)
                    :(
                      <button type="button" className="btn btn-info btn-flat" style={{height:'40px', borderRadius:'5px'}}
                        onClick={imagesAdd}>Нэмэх
                      </button>
                    ))
                  }
                </div>
                {error && <div style={{fontSize:18, color:'red'}}><strong>Алдаа гарлаа!!! {error}</strong></div>}
              </div>
              <div className="row" style={{marginTop:15}}>
                {imagesProgress > 0 ?
                <>
                  <div className="col-sm-5">
                    <div className="progress">
                      <div className="progress-bar bg-primary progress-bar-striped" role="progressbar" style={{width: `${imagesProgress}%`}} />
                    </div>
                  </div>
                  <div className="col-sm-1"><div style={{marginTop:-5, textAlign:'left'}}>{imagesProgress}%</div></div>
                </>
                :onImages &&
                onImages.map((image,index)=>{
                    return(
                      <div key={index} className="col-sm-2">
                        <img src={URL.createObjectURL(image)} className="img-fluid mb-2" alt="black sample" style={{height:'200px', marginTop:'5px'}}/>
                        <div className="row">
                          <div className="col-sm-6">{index+1}</div>
                          <div className="col-sm-6">
                            <button 
                              style={{position:'absolute',bottom:0, right:0,cursor:'pointer',border:'none',color:'white',backgroundColor:'lightcoral'}}
                              onClick={()=>setOnImages(onImages.filter((e) => e !== image))}
                            >Delete
                            </button>
                          </div>
                        </div>
                      </div>     
                    )
                  })
                }
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="row">
        <div className="col-12">
          <div className="card card-primary">
            <div className="card-header"><h4 className="card-title">Нэг мэдээлэлд 10 дээш зураг орох боломжгүй</h4></div>
            <div className="card-body">
              <div className="row" style={{marginTop:15}}>
                {images &&
                  images.map((image,index)=>{
                    return(
                      <div key={index} className="col-sm-2">
                        <img src={`${hostImageUrl}news/images/${image}`} className="img-fluid mb-2" alt="black sample" style={{height:'200px', marginTop:'5px'}}/>
                        <div className="row">
                          <div className="col-sm-6">{index+1}</div>
                          <div className="col-sm-6">
                            <button 
                              style={{position:'absolute',bottom:0, right:0,cursor:'pointer',border:'none',color:'white',backgroundColor:'lightcoral'}}
                              onClick={()=>deleteImage(image)}
                            >Delete
                            </button>
                          </div>
                        </div>
                      </div>   
                    )
                  })
                }
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
