import React, { useEffect, useState } from 'react'

export default function VacationImageShow({vimagesdelete,index,vimageURL}) {
    const [isValid, setIsValid] = useState(false);
    const [imageU, setImageU] = useState("http://localhost:8000/photos/no-image.png");

  useEffect(() => {
    fetch(vimageURL).then(res => {
      setIsValid(res.status === 200);
      if(res.status === 200){setImageU(vimageURL)}
    }).catch((err)=>console.log(err));
  }, []);
  if (!isValid) {
    return null;
  }
  return (
    isValid &&
    <div className="col-sm-2">
        <img src={imageU} className="img-fluid mb-2" alt="black sample" style={{height:'200px', marginTop:'5px'}}/>
        <div className="row">
            <div className="col-sm-6">{index+1}</div>
            <div className="col-sm-6">
                <button 
                    style={{position:'absolute',bottom:0, right:0,cursor:'pointer',border:'none',color:'white',backgroundColor:'lightcoral'}}
                    onClick={()=>vimagesdelete(index)}
                >Delete
                </button>
            </div>
        </div>
    </div> 
  )
}
