import React, { useEffect,useState,useRef, useContext } from 'react';
import { useParams }  from "react-router-dom";
import axios from '../components/Axios';
import Spinner from '../components/spinner';
import UserContext from '../context/UserContext';
export default function FormUser(props) {
  const userCtx = useContext(UserContext);
  let { id } = useParams();
  const userInachal = {
    userid:null,
    role:"",
    profileimage:null,
    lastname:"",
    firstname:"",
    email:"",
    phone:"",
    gender:"",
    birthdate:"",
    passport:["","",""],
    address:["","","",""],
    error:null,
    loading:false
  }
  const [state, setState]= useState(userInachal);

  const districtdiv = useRef();
  const khoroodiv = useRef();
  const sumdiv = useRef();

  useEffect(() => {
    setState({...state, loading:true});
    if(id){
      axios.get('users/'+id,{
        headers:{Authorization:`Bearer ${userCtx.userState.token}`}
      })
      .then((result) => {
        const UResult = result.data.data;
        const birthdate = ""+UResult.birthdate;
        const passportend = ""+UResult.passportend;
        const passport = ""+UResult.passportnumber;
        setState({
          userid: UResult._id,
          role: UResult.role,
          profileimage: null,
          lastname: UResult.lastName,
          firstname: UResult.firstName,
          email: UResult.email,
          phone: UResult.phone,
          gender: UResult.gender,
          birthdate: birthdate.substring(0, 10),
          passport: [passport.substring(0, 1),passport.substring(1),passportend.substring(0, 10)],
          address: UResult.address,
          error: null,
          loading: false
        })
      })
      .catch((err) => setState({...userInachal, loading:false,error:err.response.data.error.MNMessage}));
    }
  },[id]);
