import React, { useContext, useEffect, useState } from 'react'
import { Link }  from "react-router-dom";
import Spinner from '../spinner'
import UserContext from '../../context/UserContext';
import axios from '../Axios';
export default function BlogDataTable() { 
  const userCtx = useContext(UserContext);
  const [loading, setLoading] = useState(false);
  const [blogers, setBlogers] = useState([]);
  const [error, setError] = useState(null);
  useEffect(()=>{
    setLoading(true);
    axios.get(`users?role=blog`,{
      headers:{Authorization:`Bearer ${userCtx.userState.token}`}
    }).then((result)=>{
      setBlogers(result.data.data); setLoading(false);
    }).catch((err)=>{
      setLoading(false);
      err.response.data.error ? setError(err.response.data.error.message) : setError("Алдаа.");
    })      
    },[]);
  return (
    <div className="col-lg-12">
      <div className="card">
        <div className="card-body">
        {error && <div style={{fontSize:18, color:'red'}}><strong>Алдаа гарлаа!!! {error}</strong></div>}
        {loading ? <Spinner /> :
          <table id="example1" className="table table-bordered table-striped">
            <thead>
              <tr>
                <th>№</th>
                <th>Овог</th>
                <th>Нэр</th>
                <th>Утас</th>
                <th>Блог</th>
              </tr>
            </thead>
            <tbody>
              <tr style={{display:"none"}}><td></td><td></td><td></td><td></td><td></td></tr>
              {blogers && blogers.map((option,index) => (
                <tr key={index}>
                  <td>{index+1}</td>
                  <td>{option.lastName}</td>
                  <td>{option.firstName}</td>
                  <td>{option.phone}</td>
                  <td>
                    <Link to={`../bloglist/${option._id}`}>
                      <button type="button" className="btn btn-block btn-primary btn-sm">Жагсаалт</button>
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        }
        </div>
      </div>
    </div>
  )
}
