import React, { useContext, useEffect, useRef, useState } from 'react'
import { Link, useNavigate, useParams } from 'react-router-dom';
import axios from '../../components/Axios';
import UserContext from '../../context/UserContext';
import VacationContext from '../../context/VacationContext';
export default function VacationImage() {
  let { id } = useParams();
  const vacationCtx = useContext(VacationContext);
  const userCtx = useContext(UserContext);
  const navigate = useNavigate();
  const [vname, setVname] = useState(null);
  const [hostImageUrl] = useState("http://localhost:8000/photos/");

  const [vbackgroundImage, setVbackgroundImage] = useState(hostImageUrl+"no-image.png");
  const [vnbackgroundImage, setVnbackgroundImage] = useState(null);
  const [vcoverprogress, setVcoverprogress] = useState(null);
  const [vimages, setVimages] = useState([]);
  const [vnimages, setVnimages] = useState([]);
  const [vimagesprogress, setVimagesprogress] = useState(null);
  const [verror, setVError] = useState(null);
  useEffect(()=>{
    if(id){
      axios.get("vacations/"+id).then((result)=>{
        const resultData = result.data.data;
        setVname(resultData.name);
        if(resultData.backgroundImage){setVnbackgroundImage(resultData.backgroundImage);}
        setVname(resultData.name);
        const vimagearr = []; 
        for (let file of resultData.images) {
          fetch(file).then(res => {
            if(res.status === 200) {vimagearr.push(res.url)}
          }).catch((err)=>false)
        }
        console.log([...vimagearr])
        setVnimages([...vimagearr])
      }).catch((err)=>{
        setVError(err); console.log(err)
      })
    }
  },[]);

  // const deleteImage =(e)=>{
  //   if(id){

  //   }
  // }

  const coverImageValue = useRef();
  const coverImageAdd = ()=>{
    if(coverImageValue.current && coverImageValue.current.files[0]){
      const fd = new FormData();
      fd.append('file', coverImageValue.current.files[0], coverImageValue.current.files[0].name);
      if(id){
        axios({url:"vacations/"+id+"/coverphoto", method:'PUT', data: fd, headers:{
          Authorization:`Bearer ${userCtx.userState.token}`}
          ,onUploadProgress: progressEvent =>{
            let percentage = Math.floor((progressEvent.loaded / progressEvent.total) * 100);
            setVcoverprogress(percentage);
          }
        }).then((result)=>{
          setVbackgroundImage(null); setVcoverprogress(null);
          navigate(`../vacations/${vacationCtx.vacationState.consultantid}`);
        }).catch((err)=>{
          setVError(err); setVbackgroundImage(null); setVcoverprogress(null);
        })
      }
    }
  }
  const refimages = useRef();
  const imageAdd = ()=>{
    if(refimages.current && refimages.current.files[0]){
      const fd = new FormData();
      for (let file of refimages.current.files) {
        fd.append('file', file, file.name);
      }
      if(id){
        axios({url:"vacations/"+id+"/photo", method:'PUT', data: fd, headers:{
          Authorization:`Bearer ${userCtx.userState.token}`}
          ,onUploadProgress: progressEvent =>{
            let percentage = Math.floor((progressEvent.loaded / progressEvent.total) * 100);
            setVimagesprogress(percentage);
          }
        }).then((result)=>{
          console.log(result); setVimages([]); setVimagesprogress(null)
        }).catch((err)=>{
          console.log("aldaa==>",err)
          setVError(err); setVimages([]); setVimagesprogress(null)
        })
      }
    }
  }
  const onSelectimage = (e)=>{
    const selectedFiles = e.target.files;
    //console.log(selectedFiles[0]);
    const selectedFilesArray = Array.from(selectedFiles);
    const imagesArray = selectedFilesArray.map((file) =>{
      return URL.createObjectURL(file);
    })
    setVimages(imagesArray)
  }
  const onSelectcoverimage = (e)=>{
    const selectedFiles = e.target.files;
    const selectedFilesArray = Array.from(selectedFiles);
    const imagesArray = selectedFilesArray.map((file) =>{
      return URL.createObjectURL(file);
    })
    setVbackgroundImage(...imagesArray)
  }

  return (
    <div className="content-wrapper">
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-10">
              <h1 className="m-0">{vacationCtx.vacationState.mcode} кодтой {vname}</h1>
            </div>{/* /.col */}
            <div className="col-sm-2">
              <Link to={`../vacations/${vacationCtx.vacationState.consultantid}`}>
                <button type="button" className="btn btn-block btn-primary btn-sm">Буцах</button>
              </Link>
            </div>
          </div>{/* /.row */}
        </div>{/* /.container-fluid */}
      </div>
      <div className="content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-lg-12">
              {verror && <div style={{fontSize:18, color:'red'}}><strong>Алдаа гарлаа!!!</strong></div>}
              <div className="card card-primary">
                <div className="card-header">
                  <h3 className="card-title">Хөтөлбөрийн үндсэн зураг</h3>
                </div>
                <div className="card-body">
                  <div className="row">
                    <div className="col-sm-5">
                      <section>
                        <label 
                          style={{ 
                            display:'flex',
                            justifyContent:'center',
                            alignItems:'center',
                            border:'1px dotted black',
                            borderRadius:'5px',
                            cursor:'pointer',
                            height:'40px'
                          }}
                        >Үндсэн зураг сонгох
                          <input type="file" ref={coverImageValue} name="coverimage" style={{display:'none'}} 
                            onChange={onSelectcoverimage} accept='image/jpg, image/jpeg, image/png'
                          />
                        </label>
                      </section>
                    </div>
                    {vbackgroundImage !== hostImageUrl+"no-image.png" && 
                      <div className="col-sm-5">
                        <button type="button" className="btn btn-info btn-flat" onClick={coverImageAdd} 
                          style={{height:'40px', borderRadius:'5px'}}>Хадгалах
                        </button>
                      </div>
                    }
                  </div>
                  <div className="row" style={{marginTop:15}}>
                    {vcoverprogress > 0 ?
                    <>
                      <div className="col-sm-5">
                        <div className="progress">
                          <div className="progress-bar bg-primary progress-bar-striped" role="progressbar" style={{width: `${vcoverprogress}%`}}>
                          </div>
                        </div>
                      </div>
                      <div className="col-sm-1">
                        <div style={{marginTop:-5, textAlign:'left'}}>{vcoverprogress}%</div>
                      </div>
                    </>
                    : vbackgroundImage  &&
                      <div className="col-sm-6">
                        <img src={vbackgroundImage} className="img-fluid mb-12" alt="white sample" />
                      </div>
                    }
                    {vnbackgroundImage &&
                      <div className="col-sm-6">
                        <img src={`${hostImageUrl}vacationcover/${vnbackgroundImage}`} className="img-fluid mb-12" alt="white sample" />
                      </div>
                    }
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-12">
              <div className="card card-primary">
                <div className="card-header"><h4 className="card-title">Аяллын альбомд зураг нэмэх</h4></div>
                <div className="card-body">
                  <div className="row">
                  <div className="col-sm-5">
                      <section>
                        <label 
                          style={{ 
                            display:'flex',
                            justifyContent:'center',
                            alignItems:'center',
                            border:'1px dotted black',
                            borderRadius:'5px',
                            cursor:'pointer',
                            height:'40px'
                          }}
                        >Зураг сонгох
                          <input type="file" ref={refimages} name="coverimage" style={{display:'none'}} multiple
                            onChange={onSelectimage} accept='image/jpg, image/jpeg, image/png'
                          />
                        </label>
                      </section>
                    </div>
                    <div className="col-sm-5">
                    {vimages.length > 0 && 
                      (vimages.length > 10 ? (<div style={{color:'red'}}>10 дээш зураг нэгэн зэрэг нэмэх боломжгүй. {vimages.length - 10} зургийг устгана уу</div>)
                      :(
                        <button type="button" className="btn btn-info btn-flat" onClick={imageAdd} 
                          style={{height:'40px', borderRadius:'5px'}}>Нэмэх
                        </button>
                      ))
                    }
                    </div>
                  </div>
                  <div className="row" style={{marginTop:15}}>
                    {vimagesprogress > 0 ?
                    <>
                      <div className="col-sm-5">
                        <div className="progress">
                          <div className="progress-bar bg-primary progress-bar-striped" role="progressbar" style={{width: `${vimagesprogress}%`}}>
                          </div>
                        </div>
                      </div>
                      <div className="col-sm-1">
                        <div style={{marginTop:-5, textAlign:'left'}}>{vimagesprogress}%</div>
                      </div>
                      </>
                    :vimages &&
                      vimages.map((image,index)=>{
                        return(
                        <div key={index} className="col-sm-2">
                          <img src={image} className="img-fluid mb-2" alt="black sample" style={{height:'200px', marginTop:'5px'}}/>
                          <div className="row">
                            <div className="col-sm-6">{index+1}</div>
                            <div className="col-sm-6">
                              <button 
                                style={{position:'absolute',bottom:0, right:0,cursor:'pointer',border:'none',color:'white',backgroundColor:'lightcoral'}}
                                onClick={()=>setVimages(vimages.filter((e) => e !== image))}
                              >
                                Delete
                              </button>
                            </div>
                          </div>
                        </div>     
                        )
                      })
                    }
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-12">
              <div className="card card-primary">
                <div className="card-header"><h4 className="card-title">Аяллын альбом 30 дээш зураг орох боломжгүй</h4></div>
                <div className="card-body">
                  <div className="row">
                  </div>
                  <div className="row" style={{marginTop:15}}>
                    {vnimages &&
                      vnimages.map((image,index)=>{
                        return(
                        <div key={index} className="col-sm-2">
                          
                            <img src={`${hostImageUrl}vacation/${image}`} className="img-fluid mb-2" alt="black sample" style={{height:'200px', marginTop:'5px'}}/>
  
                          
                          <div className="row">
                            <div className="col-sm-6">{index+1}</div>
                            <div className="col-sm-6">
                              <button 
                                style={{position:'absolute',bottom:0, right:0,cursor:'pointer',border:'none',color:'white',backgroundColor:'lightcoral'}}
                                onClick={()=>setVnimages(vnimages.filter((e) => e !== image))}
                              >
                                Delete
                              </button>
                            </div>
                          </div>
                        </div>   
                        )
                        
                      })
                    }
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
