import React, { useState } from 'react'
import { Link, useNavigate, useParams } from 'react-router-dom'

export default function FaqForm() {
  let { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [faq, setFaq] = useState({ask:"", answer:""});
  const [error, setError] = useState(null);
  const handleType = (e)=>{
    setError(null);
    const {name, value} = e.target;
    setFaq((stateBefore)=>({...stateBefore, [name]:value,}));
  }
  const createHandle = ()=>{
    
  }
  const saveHandle = ()=>{
    
  }
  const deleteHandle = ()=>{
    
  }

  return (
    <div className="content-wrapper">
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-10">
              <h1 className="m-0">Асуулт хариулт {id ? "засах":"нэмэх"}</h1>
            </div>{/* /.col */}
            <div className="col-sm-2">
              <Link to={`../news`}>
                <button type="button" className="btn btn-block btn-primary">Буцах</button>
              </Link>
            </div>
          </div>{/* /.row */}
        </div>{/* /.container-fluid */}
      </div> 
      <div className="content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-12">
              <div className="card card-primary">
                {error && <div style={{fontSize:18, color:'red',marginLeft:20}}><strong>Алдаа гарлаа!!! {error}</strong></div>}
                <div className="card-body">
                <div className="row">
                  <div className="col-sm-12">
                    <label htmlFor="Selectcoin">Асуулт</label>
                    <input type="text" className={`form-control`} name="ask" placeholder="Асуулт" defaultValue={faq.ask} onChange={handleType}/>
                  </div>
                </div>
                <div className="row">
                  <div className="col-sm-12">
                    <label htmlFor="Selectrole">Хариулт</label>
                    <textarea className={`form-control`} rows={8} placeholder="Хариулт" name="answer" defaultValue={faq.answer} onChange={handleType} />
                  </div>
                </div>
                </div>
                <div className="card-footer">
                {id ?
                  <>
                    <button className={`btn btn-primary ${(faq.ask && faq.answer) ? "" : " disabled"}`} 
                      disabled={!(faq.ask && faq.answer)} style={{float:'right'}} onClick={saveHandle}
                    >Хадгалах</button>
                    <button className="btn btn-primary btn-danger" onClick={deleteHandle}>Устгах</button>
                  </>
                :
                  <button className={`btn btn-primary ${(faq.ask && faq.answer) ? "" : " disabled"}`} 
                    disabled={!(faq.ask && faq.answer)} style={{float:'right'}} onClick={createHandle}>
                  Нэмэх</button>
                }
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
