import React, { useContext, useEffect, useState } from 'react'
import axios from '../Axios';
import { Link, useParams }  from "react-router-dom";
import UserContext from '../../context/UserContext';
import Spinner from '../spinner';
import SmallSpinner from '../smallSpinner';
export default function CommentForm(pcate, psetForm) {
  let { id } = useParams();
  const userCtx = useContext(UserContext);
  const [loading, setLoading] = useState(false);
  const [userId, setUserId] = useState(null);
  const [searchNumber, setSearchNumber] = useState(null);
  const [searchLoading, setSearchLoading] = useState(false);
  const [userName, setUserName] = useState({imageUrl:null, name:null});
  
  const [comment, setComment] = useState(null);
  const [error, setError] = useState(null);
  
  useEffect(()=>{
    if(id){
      setLoading(true);
      axios.get('comment/'+id).then((result)=>{
        const resultData = result.data.data;
        setCate(resultData.cate);
        setComment(resultData.comment);
        setUserId(resultData.userId);
        setCateId(resultData.cateId);
        setLoading(false);
      }).catch((err)=>{setLoading(false); err.response.data.error ? setError(err.response.data.error.message) : setError("Алдаа.");})
    }
  },[])
  const phoneSearch = (e) =>{
    if(e.target.value.length === 8){
      setSearchLoading(true);
      axios.get('users?select=_id lastName firstName profileImage&role=user&phone='+e.target.value,{
            headers:{Authorization:`Bearer ${userCtx.userState.token}`}
      }).then((result)=>{
        const resultData = result.data.data[0]; setSearchLoading(false);
        if(resultData){
          setUserId(resultData._id); setUserName({imageUrl:resultData.profileImage, name:resultData.firstName+" "+resultData.lastName});
        }else{setUserId(null); setUserName({imageUrl:null, name:null}); setSearchNumber(e.target.value); e.target.value="";}
      }).catch((err)=>{setSearchLoading(false); err.response.data.error ? setError(err.response.data.error.message) : setError("Алдаа.");})
    }
  }

  const cateSelect = (e)=>{
    setCate(null); setCateValue([]); setCateId(null);
    if(e.target.value === "1"){
      setCateLoading(true);
      axios.get('vacations?select=_id name')
      .then((result)=>{
        const newResult = [];
        result.data.data[0] && result.data.data.map((option, index)=>{
          return newResult.push({id:option._id,value:option.name})
        })
        setCate(1); setCateValue(newResult); setCateLoading(false);
      }).catch((err)=>{setCateLoading(false); err.response.data.error ? setError(err.response.data.error.message) : setError("Алдаа.");})
    }else if(e.target.value === "2"){
      setCateLoading(true);
      axios.get('news?select=_id title')
      .then((result)=>{
        const newResult = [];
        result.data.data[0] && result.data.data.map((option, index)=>{
          return newResult.push({id:option._id,value:option.title})
        })
        setCate(2); setCateValue(newResult); setCateLoading(false);
      }).catch((err)=>{setCateLoading(false); err.response.data.error ? setError(err.response.data.error.message) : setError("Алдаа.");})
    }else if(e.target.value === "3"){
        setCateLoading(true);
        axios.get('blog?select=_id title')
        .then((result)=>{
          const newResult = [];
          result.data.data[0] && result.data.data.map((option, index)=>{
            return newResult.push({id:option._id,value:option.title})
          })
          setCate(3); setCateValue(newResult); setCateLoading(false);
        }).catch((err)=>{setCateLoading(false); err.response.data.error ? setError(err.response.data.error.message) : setError("Алдаа.");})
      }
  }
  const createDataValue = () =>{
    return {cate:cate,comment:comment,userId:userId,cateId:cateId,}
  }
  const createComment = ()=>{
    setLoading(true);
    axios.post("comment", createDataValue(),{headers:{Authorization:`Bearer ${userCtx.userState.token}`}})
    .then((result)=>{
      setLoading(false);
      navigate("/comment")
    }).catch((err)=>{
      err.response.data.error ? setError(err.response.data.error.message) : setError("Алдаа.");
      setLoading(false);
      navigate("/comment")
    })
  }
  const saveComment = ()=>{
    
  }
  const deleteComment = ()=>{

  }

  return (
    <div className="content-wrapper">
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-10">
              <h1 className="m-0">Сэтгэгдэл {id ? "засах":"нэмэх"}</h1>
            </div>{/* /.col */}
            <div className="col-sm-2">
              <Link to={`../comment`}>
                <button type="button" className="btn btn-block btn-primary btn-sm">Буцах</button>
              </Link>
            </div>
          </div>{/* /.row */}
        </div>{/* /.container-fluid */}
      </div> 
      <div className="content">
        <div className="container-fluid">
        {error && <div style={{fontSize:18, color:'red',marginLeft:20}}><strong>Алдаа гарлаа!!! {error}</strong></div>}
        {loading ? <Spinner /> :
        <>
          <div className="row">
            <div className="col-sm-2">
              <label htmlFor="Selectrole">Хэрэглэгч хайх</label>
              <input type="number" className={`form-control`} name="userId" max={8} placeholder="Утасны дугаар" onChange={phoneSearch}/>
            </div>
            <div className="col-sm-3">
              <label htmlFor="Selectrole">Хэрэглэгч</label>
              {searchLoading ? <SmallSpinner />:
              userId ?
                <div style={{display:'flex', flexDirection:'row'}}>
                  <div>zurag{userName.imageUrl}</div>
                  <div>:{userName.name}</div>
                </div>
               :<><div style={{color:'red'}}>{searchNumber}</div><div style={{color:'red'}}>Хэргэлэгч олдсонгүй</div></>
              }
            </div>
          </div>
          <div className="row">
            <div className="col-sm-12">
              <label htmlFor="Selectrole">Сэтгэгдэл</label>
              <textarea className={`form-control ${comment ? "" : " is-invalid"}`} 
                rows={3} placeholder="Сэтгэгдэл" name="v" defaultValue={comment} onChange={(e)=>{setComment(e.target.value)}}
              />
            </div>
          </div>
          <div className="card-footer">
          {id ?
            <>
              <button className={`btn btn-primary ${(userId && pcate && pcateId && comment) ? "" : " disabled"}`}
                disabled={!(userId && cate && cateId && comment)} style={{float:'right'}} onClick={saveComment}>Хадгалах</button>
              <button className="btn btn-primary btn-danger" onClick={deleteComment}>Устгах</button>
            </>
          :
            <button className={`btn btn-primary ${(userId && cate && cateId && comment) ? "" : " disabled"}`} 
              disabled={!(userId && cate && cateId && comment)} style={{float:'right'}} onClick={createComment}>
            Нэмэх</button>
          }
          </div>
        </>
        }
          
        </div>
      </div>
    </div>
  )
}
