import React,{ useState } from 'react'
import PropTypes from 'prop-types'
import './draggable-list.css'
import DraggableListItem from './DraggableListItem'
function DraggableList(props) {
    console.log(props.data)
    const [data, setData] = useState(props.data);
    const [dragStartIndex, setDragStartIndex] = useState(null)
    const onDragStart = (index) => setDragStartIndex(index)
    const onDrop = (dropIndex) =>{
        //console.log(dropIndex)
        const dragItem = data[dragStartIndex]
        let list = [...data]
        list.splice(dragStartIndex,1)
        if(dragStartIndex < dropIndex){
            setData([...list.slice(0,dropIndex -1 ),dragItem,...list.splice(dropIndex -1, list.length)])
        }else{setData([...list.slice(0,dropIndex),dragItem,...list.splice(dropIndex, list.length)])}
    }
  return (
    <ul className='draggable-list'>
        { data &&
            data.map((item, index) =>(
                <DraggableListItem 
                    key={index} 
                    index={index} 
                    onDragStart={(index) => onDragStart(index)}
                    onDrop={(index)=>onDrop(index)}
                >{item}</DraggableListItem>
            ))
        }
        <DraggableListItem key={data.length} index={data.length} onDrop={(index)=>onDrop(index)} draggale={false}/>
    </ul>
  )
}

DraggableList.propTypes = {
    data: PropTypes.array
}

export default DraggableList
