import React, { useEffect,useState } from 'react'
import { useParams }  from "react-router-dom";
export default function FormAdmin() {
  let { id } = useParams();
  const [state, setState]= useState({
    userid:null,
    role:null,
    profileimage:null,
    lastname:null,
    firstname:null,
    email:null,
    phone:null,
    gender:null,
    birthdate:null,
    passport:[],
    address:[],
    error:null,
    success:null
  });
  useEffect(() => {
    //setState({...state, loading:true});
    if(id){
      //axios.get('books/'+id)
      //.then((result) => setState({...result.data.data,error:null,loading:false}))
      //.catch((err) => setState({error: err.response.data.error.message, loading:false}));
      setState({userid:id,role:'Admin',profileimage:1,lastname:'TS',firstname:'BASMAA',email:'tengertravel39@gmail.com',phone:'95953939',gender:'Female',birthdate:'1988-09-12',passport:['E','8754213','2032-12-01'],address:['Улаанбаатар','Хан-Уул','8-р хороо','14-35 тоот']});
    }
  },[id]);

  const city = ['Архангай','Баян-Өлгий','Баянхонгор','Булган','Говь-Алтай','Говьсүмбэр','Дархан-Уул','Дорноговь','Дорнод','Дундговь','Завхан','Орхон','Сэлэнгэ','Сүхбаатар','Төв','Увс','Ховд','Хэнтий','Хөвсгөл','Өвөрхангай','Өмнөговь'];
  const citydis = [{Архангай:['Батцэнгэл','Булган','Жаргалант','Ихтамир','Тариат','Төвшрүүлэх','Хайрхан','Хангай',' Хашаат','Хотонт','Цахир','Цэнхэр','Цэцэрлэг','Чулуут','Эрдэнэбулган','Эрдэнэмандал','Өгийнуур','Өлзийт','Өндөр-Улаан']}];

  const district = ['Хан-уул','Сүхбаатар','Сонгинохайрхан','Баянзүрх','Баянгол','Чингэлтэй','Налайх','Багануур','Багахангай'];
  const horoo = [];
    for(var i = 1; i <=50; i++) {
      var obj = {}; obj['value'] = 1; obj['label'] = i+"-р хороо";
      horoo.push(obj);
    }
    const [aimagsum,setAimagsum] = useState([]);
    
    const citychange = (e)=>{
      const {name, value} = e.target; let selectedaddress;
      
      if(name==="addresscity"&&value==="Улаанбаатар"){
        document.getElementById("districtdiv").style.display="";
        document.getElementById("khoroodiv").style.display="";
        document.getElementById("sumdiv").style.display="none";
        setAimagsum([]);
      }else{
        document.getElementById("districtdiv").style.display="none";
        document.getElementById("khoroodiv").style.display="none";
        document.getElementById("sumdiv").style.display="";
        const aimagsumar=[]; citydis[0].value[0].map((option) => aimagsumar.push(option))
        setAimagsum(aimagsumar);
        alert(name);
      }
     
      if(name==="addresscity"){ selectedaddress=[value,state.address[1],state.address[2],state.address[3]]}
      
      setState({...state, address:selectedaddress, error:null,success:null});
    }
  const handleType = (e) =>{
    const {name, value} = e.target;
    if(name==='passportser'){ setState({...state, passport:[value,state.passport[1],state.passport[2]], error:null,success:null}); }
    else if(name==='passportnumber'){ setState({...state, passport:[state.passport[0],value,state.passport[2]], error:null,success:null}); }
    else if(name==='passportend'){ setState({...state, passport:[state.passport[0],state.passport[1],value], error:null,success:null}); }
    else{ setState({...state, [name]:value, error:null,success:null}); }
  }
  return (
  <div className="col-lg-7">
    <div className="card card-primary">
      <div className="card-header">
        <h3 className="card-title">Үнэн зөв, бүрэн бөглөнө үү.</h3>
      </div>
      <form>
        <div className="card-body">
          <div class="row">
            <div class="col-sm-6">
            <label htmlFor="Selectrole">Role</label>
              <select class="form-control" id="Selectrole" value={state.role}>
                <option>Operator</option>
                <option>Sub admin</option>
                <option>Admin</option>
              </select>
            </div>
            <div class="col-sm-6">
              <label htmlFor="profileimage">Profile image</label>
              <div className="input-group">
                <div className="custom-file">
                  <input type="file" className="custom-file-input" id="profileimage" />
                  <label className="custom-file-label" htmlFor="profileimage">Profile image</label>
                </div>
              </div>
            </div>
          </div>
          <div class="row">
            <div class="col-sm-6">
              <label htmlFor="Inputlastname">Last Name</label>
              <input type="text" className="form-control" name="lastname" placeholder="Last Name" value={state.lastname} onChange={handleType}/>
            </div>
            <div class="col-sm-6">
              <label htmlFor="Inputfirstname">First Name</label>
              <input type="text" className="form-control" name="firstname" placeholder="First Name" value={state.firstname} onChange={handleType}/>
            </div>
          </div>
          <div class="row">
            <div class="col-sm-6">
              <label htmlFor="Inputemail">Email</label>
              <input type="email" className="form-control" name="email" placeholder="Email" value={state.email} onChange={handleType}/>
            </div>
            <div class="col-sm-6">
              <label htmlFor="Inputphone">Phone number</label>
              <input type="phone" className="form-control" name="phone" placeholder="Phone number" value={state.phone} onChange={handleType}/>
            </div>
          </div>
          <div class="row">
            <div class="col-sm-6">
              <label htmlFor="Selectgender">Gender</label>
              <select class="form-control" name="gender" value={state.gender} onChange={handleType}>
                <option>Select</option>
                <option>Male</option>
                <option>Female</option>
              </select>
            </div>
            <div class="col-sm-6">
              <label htmlFor="Inputbirthdate">Birthdate</label>
              <input type="date" className="form-control" name="birthdate" placeholder="Birthdate" value={state.birthdate} onChange={handleType}/>
            </div>
          </div>
          <div class="row">
            <div class="col-sm-2">
              <label htmlFor="Selectser">Ser</label>
              <select class="form-control" name="passportser" value={state.passport[0]} onChange={handleType}>
                <option>E</option>
                <option>A</option>
                <option>D</option>
                <option>G</option>
                <option>EC</option>
                <option>NULL</option>
              </select>
            </div>
            <div class="col-sm-4">
              <label htmlFor="Inputpassportnumber">Passport number</label>
              <input type="number" className="form-control" name="passportnumber" placeholder="Passport number" value={state.passport[1]} onChange={handleType}/>
            </div>
            <div class="col-sm-6">
              <label htmlFor="Inputpassportend">Passport end</label>
              <input type="date" className="form-control" name="passportend" placeholder="Phone end" value={state.passport[2]} onChange={handleType}/>
            </div>
          </div>
          <div class="row">
            <div class="col-sm-2">
              <label htmlFor="Selectcity">City</label>
              <select class="form-control" name="addresscity" onChange={citychange} value={state.address[0]}>
              <option value="Улаанбаатар">Улаанбаатар</option>
                {city.map((option) => (
                  <option value={option}>{option}</option>
                ))}
              </select>
            </div>
            <div id="districtdiv" class="col-sm-2">
              <label htmlFor="Selectdistrict">District</label>
              <select class="form-control" id="Selectdistrict" value={state.address[1]}>
                {district.map((option) => (
                  <option value={option}>{option}</option>
                ))}
              </select>
            </div>
            <div id="khoroodiv" class="col-sm-2">
              <label htmlFor="Selectkhoroo">Khoroo</label>
              <select class="form-control" id="Selectkhoroo" value={state.address[2]}>
              {horoo.map((option) => (
                <option value={option.value}>{option.label}</option>
              ))}
              </select>
            </div>
            <div id="sumdiv" class="col-sm-4" style={{display:'none'}}>
              <label htmlFor="Inputsum">Sum</label>
              <select class="form-control" id="Selectsum" value={state.address[1]}>
                <option value="">Select</option>
                {aimagsum.map((option) => (
                  <option value={option}>{option}</option>
                ))}
              </select>
            </div>
            <div class="col-sm-6">
              <label htmlFor="Inputaddress">Address</label>
              <input type="text" className="form-control" id="Inputaddress" placeholder="Address" value={state.address[3]}/>
            </div>
          </div>
        </div>
        {/* /.card-body */}
        <div className="card-footer">
          <button type="submit" className="btn btn-primary">Нэмэх</button>
        </div>
      </form>
    </div>
  </div>
  )
}
