import React, { useEffect, useState } from 'react'
import { Link, useParams }  from "react-router-dom";
import axios from '../Axios';
import CoverImage from '../photoUpload/Coverimage';
import NewsImages from './NewsImages';
export default function NewsPhoto() {
  let { id } = useParams();
  const [coverImgUrl, setCoverImage] = useState(null);
  const [images, setImages] = useState([]);
  const [error, setError] = useState(null);
  const [imageProgress, setImageProgress] = useState(null);
  useEffect(()=>{
    if(id){
      axios.get('news/'+id).then((result)=>{
        setCoverImage(result.data.data.coverUrl);
        setImages(result.data.data.images);
      }).catch((err)=>{err.response.data.error ? setError(err.response.data.error.MNMessage) : setError("Алдаа.");})
    }
  },[])
  const coverPhotoAdd = (photo) =>{
    if(photo){
      const fd = new FormData();
      fd.append('file', photo, photo.name);
      if(id){
        axios({url:"news/"+id+"/coverphoto", method:'PUT', data: fd, headers:{
          Authorization:`Bearer ${userCtx.userState.token}`}
          ,onUploadProgress: progressEvent =>{
            let percentage = Math.floor((progressEvent.loaded / progressEvent.total) * 100);
            setImageProgress(percentage);
          }
        }).then((result)=>{
          setOnImage(hostImageUrl+"no-image.png"); setImageProgress(null);
        }).catch((err)=>{
          err.response.data.error ? setError(err.response.data.error.MNMessage) : setError("Алдаа.");
          setOnImage(hostImageUrl+"no-image.png"); setImageProgress(null);
        })
      }
    }
  }
  return (
    <div className="content-wrapper">
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-10">
              <h1 className="m-0">Мэдээлэлд зураг {id ? "засах":"нэмэх"}</h1>
            </div>{/* /.col */}
            <div className="col-sm-2">
              <Link to={`../news`}>
                <button type="button" className="btn btn-block btn-primary btn-sm">Буцах</button>
              </Link>
            </div>
          </div>{/* /.row */}
        </div>{/* /.container-fluid */}
      </div> 
      <div className="content">
        <div className="container-fluid">
          <div className="row">
            {imageProgress 
            
            }
            <CoverImage title="Мэдээллийн үндсэн зураг" imageSize={5} folderUrl="news/cover/" photoUrl={coverImgUrl} coverPhotoAdd={coverPhotoAdd}/>
          </div>
          <NewsImages />
        </div>
      </div>
    </div>
  )
}
